import { useState, useEffect  } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import Swal from "sweetalert2";
import TeacherSidebar from "./TeacherSidebar";

const baseUrl='http://127.0.0.1:8000/api';

function EditChapter() {

    useEffect(()=>{
        document.title='Edit Chapter';
      });

      const [chapterData, setChapterData] = useState({
         course:'',
         title: '',
         description: '',
         upload: '',
         remarks: ''
       });
      

       const handleChange=(event)=>{
         setChapterData({
           ...chapterData,
           [event.target.name]: event.target.value
         });
       }
 
       const handleFileChange=(event)=>{
        setChapterData({
          ...chapterData,
          [event.target.name]: event.target.files[0]
        });
      }
       
       const {chapter_id}=useParams();

       const submitForm=()=>{
         const _formData = new FormData();
         _formData.append('course', chapterData.course.id)
         _formData.append('title', chapterData.title)
         _formData.append('description', chapterData.description)
         _formData.append('upload', chapterData.upload, chapterData.upload.name)
         _formData.append('remarks', chapterData.remarks)
     
         try{
           axios.put(baseUrl+'/chapter/'+ chapter_id + '/', _formData, {
             headers: {
                 'Content-Type': 'multipart/form-data'
             }
           }).then((res)=>{
             console.log(res);
             if(res.status===200 || res.status===201){
              Swal.fire({
                  title: 'Data updated',
                  icon: "success",
                  timer: 3000,
                  timerProgressBar: true,
                  showConfirmButton: false
              });
            }
            // window.location.href='/teacher-add-chapter/5';
           });
         }catch(error){
             console.log(error);
             
         }
     
         
     
       };

       useEffect(()=>{
         try{
             axios.get(baseUrl + '/chapter/'+ chapter_id+'/').then((res)=>{
              console.log(res.data.course);
                 setChapterData(res.data);
             });
           }catch(error){
             console.log(error);
           }
     }, []);

    return (
        <>
       <div id="back-img">
        <div class="container mt-4" >
          <div class="row">
            <aside class="col md-3">
              <TeacherSidebar/>
            </aside>
            <div class="col-9">
              <div class="card">
                 <h4 id="add-word">Курс қосу</h4>
                 <div class="card-body">
        <form>
        <div className="form-group">
             <input name="title" value={chapterData.title} onChange={handleChange} type="text" className="form-control" id="InputTitle" placeholder="Бөлімнің атауы"></input>
           </div>

           <div className="form-group">
             <textarea name="description" value={chapterData.description} onChange={handleChange} className="form-control" id="InputDescription" placeholder="Бөлімнің сипаттамасы"></textarea>
           </div>

           <div className="form-group" style={{paddingTop: '21px'}}>
               <input name="upload" onChange={handleFileChange} type="file" className="form-control" id="inputGroupFile03" aria-label="Upload" ></input>
           </div>

           <div className="form-group">
             <textarea name="remarks" value={chapterData.remarks} onChange={handleChange} className="form-control" id="InputTechnologies" placeholder="Ескертулер"></textarea>
           </div>
           
           <div className="form-group">
             <button type="button" onClick={submitForm}>Жүктеу</button>
           </div>

         </form>
         </div>
              </div>
            </div> 
          </div>
        </div>
        </div> 
        </>
    );
}
export default EditChapter;
