import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import './TeacherMyCourses.css';
import MessageList from "./MessagesList";
import TeacherSidebar from "./TeacherSidebar";

const baseUrl='http://127.0.0.1:8000/api';

function TeacherMyUsers() {

  const msgList={
       height: '500px',
       overflow: 'auto'
  }

    useEffect(()=>{
        document.title='User List';
    });

    const [studentData, setStudentData] = useState([]);
    const teacherId=localStorage.getItem('teacherId');
    const [msgData, setmsgData] = useState({
      'msg_text': ''
    });
    const [successMsg, setsuccessMsg] = useState([]);
    const [errorMsg, seterrorMsg] = useState([]);
    const [groupmsgData, setgroupmsgData] = useState({
      'msg_text': ''
    });
    const [groupsuccessMsg, setgroupsuccessMsg] = useState([]);
    const [grouperrorMsg, setgrouperrorMsg] = useState([]);
    

   useEffect(()=>{
    try{
      axios.get(baseUrl + '/fetch-all-enrolled-users/'+ teacherId).then((res)=>{
        setStudentData(res.data);
      })
      }catch(error){
        console.log(error);
      }
}, []);
    
if (!studentData) {
  return <div>Loading...</div>
}



const handleChange=(event)=>{
  setmsgData({
    ...msgData,
    [event.target.name]: event.target.value
  });
}

const submitForm=(user_id)=>{
  const msgFormData = new FormData();
  msgFormData.append('msg_text', msgData.msg_text)
  msgFormData.append('msg_from', 'teacher')

  try{
    axios.post(baseUrl + '/send-message/'+teacherId+'/'+user_id, msgFormData).then((response)=>{
      if(response.data.bool===true){
        setmsgData({
          'msg_text': ''
        });
        setsuccessMsg(response.data.msg);
        seterrorMsg('');
    }else{
        seterrorMsg(response.data.msg);
        setsuccessMsg('');
    }
    });
  }catch(error){
    console.log(error);
  }


};

const groupsubmitForm=()=>{
  const msgFormData = new FormData();
  msgFormData.append('msg_text', groupmsgData.msg_text)
  msgFormData.append('msg_from', 'teacher')

  try{
    axios.post(baseUrl + '/send-group-message/'+teacherId+'/', msgFormData).then((response)=>{
      if(response.data.bool===true){
        setgroupmsgData({
          'msg_text': ''
        });
        setgroupsuccessMsg(response.data.msg);
        setgrouperrorMsg('');
    }else{
        setgrouperrorMsg(response.data.msg);
        setgroupsuccessMsg('');
    }
    });
  }catch(error){
    console.log(error);
  }


};

const grouphandleChange=(event)=>{
  setgroupmsgData({
    ...groupmsgData,
    [event.target.name]: event.target.value
  });
}


    return (
        <>

<div id="back-img">
        <div class="container mt-4" >
          <div class="row">
            <aside class="col md-3">
              <TeacherSidebar/>
            </aside>
            <div class="col-md-9">
              <div class="card">
                 <h4 id="add-word">
                  Барлық студенттер</h4>
                  <button type="button" id="another-button" class="log-but"  data-bs-toggle="modal" data-bs-target="#groupModal">
                    Жазу
                  </button>


                 {/**Modal */}
<div className="modal fade" id="groupModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div className="modal-dialog">
    <div className="modal-content">
      <div className="modal-header">
        <h1 className="modal-title fs-5" id="exampleModalLabel">Send message to all users</h1>
        <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div className="modal-body">
        
         {groupsuccessMsg && <p className='text-success'>{groupsuccessMsg}</p>}
         {grouperrorMsg && <p className='text-danger'>{grouperrorMsg}</p>}
          <form>
           <div className="mb-3">
             <textarea name='msg_text' value={groupmsgData.msg_text} className="form-control" onChange={grouphandleChange} rows='10' placeholder="Add your message here..."></textarea>
           </div>
           <button type="button"  onClick={groupsubmitForm} className="btn btn-primary">Submit</button>
          </form>

      </div>
    </div>
  </div>
</div>
       



                 <div class="card-body">
<table className="table table-bordered">
  <thead>
    <tr>
      <th scope="col">Full name</th>
      <th scope="col">Email</th>
      <th scope="col">Write</th>
    </tr>
  </thead>
  <tbody>
  {studentData.map((row, index)=> (
            <tr key={index}>
              <td>{row.user.full_name}</td>
              <td>{row.user.email}</td>
              <td><button id="chat-button" data-bs-toggle="modal" data-bs-target={`#msgModal${index}`}><i className="bi bi-chat-dots-fill"></i></button>

{/**Modal */}
<div className="modal fade" id={`msgModal${index}`} tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div className="modal-dialog modal-fullscreen">
    <div className="modal-content">
      <div className="modal-header">
        <h1 className="modal-title fs-5" id="exampleModalLabel">{row.user.full_name}
        </h1>
        <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div className="modal-body">

        <div className="row">
          <div className="col-md-8 mb-2 col-12 border-end" style={msgList}>
            <MessageList  teacher_id={teacherId} user_id={row.user.id}/>
          </div>

         <div className="col-md-4 col-12">
         {successMsg && <p className='text-success'>{successMsg}</p>}
         {errorMsg && <p className='text-danger'>{errorMsg}</p>}
          <form>
           <div className="mb-3">
             <textarea name='msg_text' value={msgData.msg_text} className="form-control" onChange={handleChange} rows='6' placeholder="Add your message here..."></textarea>
           </div>
           <button type="button"  onClick={()=>submitForm(row.user.id)} id="login-button">Send</button>
          </form>
         </div>
        </div>

      </div>
    </div>
  </div>
</div>
              
              </td>
            </tr>
          ))}
</tbody>
</table>
</div>
              </div>
            </div> 
          </div>
        </div>
        </div> 
   
        </>
    );
}
export default TeacherMyUsers;
