import './TeacherSignup.css';
import {Link} from 'react-router-dom';
import {useEffect, useState} from 'react';
import axios from 'axios';

function TeacherSignup() {

  useEffect(()=>{
    document.title='Teacher Sign UP';
  });

  const [teacherData, setteacherData] = useState({
    'full_name': '',
    'email': '',
    'password': '',
    'qualification': '',
    'mobile_no': '',
    'skills': '',
    'status': ''
  });

  const handleChange=(event)=>{
    setteacherData({
      ...teacherData,
      [event.target.name]: event.target.value
    });
  }
  console.log(teacherData);

  return (
    <> 
        {/* Left Part */}
       <div className="leftSide"></div>
        
        {/* Right Part */}
       <div className="rightSide">
        
         <Link to="/"><p className="logo" >lulu school</p></Link>
         <div id = "register-info">
         <h3>Аккаунтқа тіркелу</h3>
         <form>
           <div className="form-group">
             <input name="full_name" onChange={handleChange} type="text" className="form-control" id="InputUsername1" aria-describedby="emailHelp" placeholder="Аты - жөні"></input>
           </div>

           <div className="form-group">
             <input name="email" onChange={handleChange} type="email" className="form-control" id="InputEmail1" aria-describedby="emailHelp" placeholder="Электрондық пошта"></input>
           </div>

           <div className="form-group">
             <input name="password" onChange={handleChange} type="password" className="form-control" id="InputPassword1" placeholder="Құпия сөз"></input>
           </div>

           <div className="form-group">
             <input name="qualification" onChange={handleChange} type="text" className="form-control" id="InputQualification1" aria-describedby="emailHelp" placeholder="Біліктілік"></input>
           </div>

           <div className="form-group">
             <input name="mobile_no" onChange={handleChange} type="number" className="form-control" id="InputNumber1" aria-describedby="emailHelp" placeholder="Тел. нөмірі"></input>
           </div>

           <div className="form-group">
             <textarea name="skills" onChange={handleChange} type="text" className="form-control" id="InputSkills" aria-describedby="emailHelp" placeholder="Қабілеттілік пен дағдылар"></textarea>
           </div>
           
           <div className="form-group">
             <button type="submit">Тіркелу</button>
           </div>

         </form>
         <p id="bottom-words">Тіркеліп қойдыңыз ба? <Link to="/teacher-login">Кіру</Link></p>
         </div>
         </div>

    </>
  );
}

export default TeacherSignup;
