import { useState, useEffect  } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import Swal from "sweetalert2";

const baseUrl='http://127.0.0.1:8000/api';

function EditQuiz() {

    useEffect(()=>{
        document.title='Edit Quiz';
      });

      const teacherId=localStorage.getItem('teacherId');

    const [quizData, setquizData] = useState({
        title: '',
        detail: ''
      });

    const {quiz_id}=useParams();  
    useEffect(()=>{
          //fetch current quiz data
          try{
            axios.get(baseUrl + '/teacher-quiz-detail/'+quiz_id+'/').then((res)=>{
                setquizData({
                    title: res.title,
                    detail: res.detail
                });
            });
          }catch(error){
            console.log(error);
          }
          //End

      
    }, []);
    //console.log(cats);
    
    const handleChange=(event)=>{
        setquizData({
          ...quizData,
          [event.target.name]: event.target.value
        });
      }

      
      const submitForm=()=>{
        
        const _formData = new FormData();
        _formData.append("teacher", teacherId)
        _formData.append("title", quizData.title)
        _formData.append("detail", quizData.detail)

        try{
            axios.put(baseUrl+'/teacher-quiz-detail/'+quiz_id+'/', _formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
          }).then((res)=>{
            if(res.status == 200){
                Swal.fire({
                  title: 'updated',
                  icon: 'success',
                  toast: true,
                  timer: 3000,
                  position: 'top-right',
                  timerProgressBar: true,
                  showConfirmButton: false
                });
            }
          });
        }catch(error){
            console.log(error);
            
        }
    
        
    
      };

    return (
        <>
        <div id="background1">
    
        <div class="container mt-4" >
        <div id="my"><h4>Quiz өзгеру</h4></div>
        <form>
        <div className="form-group">
             <input name="title" value={quizData.title} onChange={handleChange} type="text" className="form-control" id="InputTitle" placeholder="Aтауы"></input>
           </div>

           <div className="form-group">
             <textarea name="detail" value={quizData.detail} onChange={handleChange} className="form-control" id="InputDescription" placeholder="Detail"></textarea>
           </div>
           
           <div className="form-group">
             <button type="button" onClick={submitForm}>Жүктеу</button>
           </div>

         </form>
         </div>
         </div>
        </>
    );
}
export default EditQuiz;
