import TeacherHeader from "./TeacherHeader";
import { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import './TeacherMyCourses.css';

const baseUrl='http://127.0.0.1:8000/api';

function QuizResult() {
    useEffect(() => {
        document.title = 'Quiz Result';
    });

    const [resultData, setResultData] = useState({});
    const { quiz_id, user_id } = useParams();

    useEffect(() => {
        axios
          .get(`${baseUrl}/fetch-quiz-result/${quiz_id}/${user_id}`)
          .then((res) => {
            setResultData(res.data);
          })
          .catch((error) => {
            console.log(error);
          });
      }, [quiz_id, user_id]);

    return (
        <>
            <TeacherHeader />
            <h1>Quiz Result</h1>
            <table className="table">
                <thead>
                    <tr>
                        <th scope="col">Total Questions</th>
                        <td>{resultData.total_questions}</td>
                    </tr>
                    <tr>
                        <th scope="col">Attempted Questions</th>
                        <td>{resultData.total_attempted_questions}</td>
                    </tr>
                </thead>
            </table>
        </>
    );
}

export default QuizResult;
