import UserHeader from "./UserHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import UserSidebar from "./UserSidebar";


const baseUrl='http://127.0.0.1:8000/api';

function TakeQuiz(){
    useEffect(()=>{
        document.title='Questions';
    });
    
    const [questionData, setquestionData] = useState([]);
    const {quiz_id} = useParams();
    console.log(quiz_id)
    const userId=localStorage.getItem('userId');


    useEffect(()=>{
      try{
          axios.get(baseUrl + '/quiz-questions/'+ quiz_id + '/1').then((res)=>{
              
              setquestionData(res.data);
          });
        }catch(error){
          console.log(error);
        }
  }, []);
    
  const submitAnswer = (question_id, right_ans) => {
    const _formData = new FormData();
    _formData.append('user', userId)
    _formData.append('quiz', quiz_id)
    _formData.append('question', question_id)
    _formData.append('right_ans', right_ans)

    try{
      axios.post(baseUrl+'/attempt-quiz/', _formData, {
        headers: {
            'Content-Type': 'multipart/form-data'
        }
      }).then((res)=>{
        console.log(res.data);
        try{
            axios.get(baseUrl + '/quiz-questions/' + quiz_id + '/next-question/' + question_id).then((res)=>{
                console.log(res.data);
                setquestionData(res.data);
            });
          }catch(error){
            console.log(error);
          }
      }).catch((error) => {
        console.log(error);
      });
    }catch(error){
        console.log(error);
    }
};

    return(
        <>
       <div id="back-img">
        <div class="container mt-4" >
          <div class="row">
            <aside class="col md-3">
              <UserSidebar/>
            </aside>
            <div class="col-md-9">
              <div class="card">
                 <h4 id="add-word">Quiz title</h4>

        {questionData.map((row, index) => 
          <>
          <div class="card" id="question">
        <h4>{row.question}</h4>
        <div class="card-body">
        <table className="table table-bordered">
         <tbody>
        
            <tr>
               <td>1</td>
               <td><button id="answer" onClick={()=>submitAnswer(row.id, row.ans1)}>{row.ans1}</button></td>
            </tr>
            <tr>
               <td>2</td>
               <td><button id="answer" onClick={()=>submitAnswer(row.id, row.ans2)}>{row.ans2}</button></td>
            </tr>
            <tr>
               <td>3</td>
               <td><button id="answer" onClick={()=>submitAnswer(row.id, row.ans3)}>{row.ans3}</button></td>
            </tr>
            <tr>
               <td>4</td>
               <td><button id="answer" onClick={()=>submitAnswer(row.id, row.ans4)}>{row.ans4}</button></td>
            </tr>
           
        
         </tbody>
        </table>
        </div>
        </div>
        </>
        )}
        </div>
              </div>
            </div> 
          </div>
          </div>
        
        </>
    );
}
export default TakeQuiz;
