import {Link, useNavigate, useParams} from 'react-router-dom';
import {useEffect, useState} from 'react';
import axios from 'axios';

const baseUrl='http://127.0.0.1:8000/api';
function VerifyUser() {

  useEffect(()=>{
    document.title='Verify';
  });

  const [userData, setuserData] = useState({
    otp_digit: ''
  });
  const [errorMSg, seterrorMsg] = useState([]);
  const {user_id}=useParams();
  const navigate=useNavigate()

  const handleChange=(event)=>{
    setuserData({
      ...userData,
      [event.target.name]: event.target.value
    });
  }

  const submitForm=()=>{
    const userFormData = new FormData();
    userFormData.append('otp_digit', userData.otp_digit)

    try{
      axios.post(baseUrl + '/user-verify/' + user_id + '/', userFormData).then((res)=>{
        if(res.data.bool===true){
          localStorage.setItem('userLoginStatus', true);
          localStorage.setItem('userId', res.data.user_id);
          //navigate('/teacher-profile');
        }else{
          seterrorMsg(res.data.msg);
        }
      });
    }catch(error){
      console.log(error);
    }


  };

  const userLoginStatus=localStorage.getItem('userLoginStatus')
  if(userLoginStatus==='true'){
    window.location.href='/user-profile';
 }

  return (
    <>
       <div className="leftPart">
         
         <div id = "register-info">
          <h3 id="header-w">Enter 6 digit OTP</h3>
          <form>
          {errorMSg && <p className='text-danger'>{errorMSg}</p>}
            <div className="form-group">
             <input name="otp_digit" value={userData.otp_digit} onChange={handleChange} type="number" className="form-control" id="InputEmail1" aria-describedby="emailHelp" placeholder="Электрондық пошта"></input>
           </div>

            <div className="form-group">
              <button id="login-button" onClick={submitForm} type="submit">Verify</button>
            </div>

          </form>
         </div>
       </div>

       {/* Right Part */}
       <div className="rightPart"></div>
    </>
  );
}

export default VerifyUser;
