import './TeacherLogin.css';
import {Link} from 'react-router-dom';
import {useEffect, useState} from 'react';

const baseUrl='http://127.0.0.1:8000/api';
function TeacherLogin() {

  useEffect(()=>{
    document.title='Teacher Log In';
  });

  const [teacherLoginData, setteacherLoginData] = useState({
    'email': '',
    'password': ''
  });

  const handleChange=(event)=>{
    setteacherLoginData({
      ...teacherLoginData,
      [event.target.name]: event.target.value
    });
  }

  return (
    <>
       {/* Left Part */}
       <div className="leftPart">
         <Link to="/"><p className="logo" >lulu school</p></Link>
         <div id = "register-info">
          <h3>Аккаунтқа кіру</h3>
          <form>

            <div className="form-group">
              <input type="text" className="form-control" id="InputUsername1" aria-describedby="emailHelp" placeholder="Аты - жөні"></input>
            </div>

            <div className="form-group">
              <input type="password" className="form-control" id="InputPassword1" placeholder="Құпия сөз"></input>
            </div>

            <div className="form-group">
              <button id="login-button" type="submit">Кіру</button>
            </div>

            <div className="form-group">
               <p>Аккаунтыңыз жоқ па? <Link to="/teacher-signup">Тіркелу</Link></p>
            </div>

          </form>
         </div>
       </div>
       
       
       {/* Right Part */}
       <div className="rightPart"></div>
    </>
  );
}

export default TeacherLogin;
