import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import './TeacherMyCourses.css';
import Swal from "sweetalert2";
import CheckQuizInCourse from "./CheckQuizInCourse";
import TeacherSidebar from "./TeacherSidebar";

const baseUrl='http://127.0.0.1:8000/api';

function AssignQuiz(){

    useEffect(()=>{
        document.title='Assign Quiz';
    });

    const [quizData, setquizData] = useState([]);
    const[assignStatus, setassignStatus]=useState();
    const [courseData, setCourseData] = useState([]); 
    const teacherId=localStorage.getItem('teacherId');
    const {course_id}=useParams();
    

    useEffect(()=>{
        try{
            axios.get(baseUrl + '/teacher-quizzes/'+ teacherId).then((res)=>{
                setquizData(res.data);
            });
          }catch(error){
            console.log(error);
          }

          try{
            axios.get(baseUrl + '/course/' + course_id).then((res)=>{
                setCourseData(res.data);
                
            });
          }catch(error){
            console.log(error);
          }


    }, []);


    const assignQuiz=(quiz_id) => {
        const _formData = new FormData();
        _formData.append("teacher", teacherId)
        _formData.append("course", course_id)
        _formData.append("quiz", quiz_id)
    
        try{
          axios.post(baseUrl+'/quiz-assign-course/', _formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
          }).then((res)=>{
            if(res.status===200||res.status===201){
                window.location.reload();
            }
          });
        }catch(error){
            console.log(error);
            
        }
    } 

    return(
        <>
        <div id="back-img">
        <div class="container mt-4" >
          <div class="row">
            <aside class="col md-3">
              <TeacherSidebar/>
            </aside>
            <div class="col-9">
              <div class="card">
                 <h4 id="add-word">Тест бекіту - ({courseData.title})</h4>
                 <div class="card-body">

                 <table className="table table-bordered pb-1">
  <tbody>
  {quizData.map((row, index)=> (
            <tr key={index}>
              <td>{index + 1}</td>
              <td><h5 className="card-title"><Link to={'/teacher-all-questions/'+ row.id} id="a">{row.title}</Link></h5></td>
              <td><CheckQuizInCourse quiz={row.id} course={course_id}/></td>
              </tr>
          ))}
</tbody>
</table>  
 
 
     </div>
              </div>
            </div> 
          </div>
        </div>
        </div>

      
        </>
    );
}
export default AssignQuiz;
