import { useState, useEffect  } from "react";
import axios from "axios";
import TeacherSidebar from "./TeacherSidebar";

const baseUrl='http://127.0.0.1:8000/api';

function TeacherChangePassword() {
    useEffect(()=>{
        document.title='Құпиясөзді өзгерту';
      });
    const teacherId=localStorage.getItem('teacherId');

    const [teacherData, setteacherData] = useState({
      'password': ''
    });

  
  const handleChange=(event)=>{
    setteacherData({
      ...teacherData,
      [event.target.name]: event.target.value
    });
  }

  const submitForm=()=>{
    const teacherFormData = new FormData();
    teacherFormData.append("password", teacherData.password)

    try{
      axios.post(baseUrl + '/teacher/change-password/'+ teacherId + '/', teacherFormData).then((response)=>{
        if(response.status===200 || response.status===201){
            window.location.href='/teacher-logout';
        }else{
            alert('Some errors occured!');
        }
      });
    }catch(error){
        console.log(error);
        setteacherData({'status': 'error'});
    }

    

  };

  const teacherLoginStatus=localStorage.getItem('teacherLoginStatus')
  if(teacherLoginStatus!=='true'){
    window.location.href='/teacher-login';
  }

    return (
        <>
        <div id="back-img">
        <div className="container mt-4" >
          <div className="row">
            <aside className="col md-3">
              <TeacherSidebar/>
            </aside>
            <div className="col-9" id="pass-change">
              <div className="card">
                 <h4 id="add-word">Құпия сөзді өзгерту</h4>
                 <div className="card-body">
        <div className="form-group">
              <input name="password" type="text" value={teacherData.password} onChange={handleChange} className="form-control" id="InputPassword1" placeholder="Құпия сөз"></input>
        </div>

        <div className="form-group">
              <button id="login-button" onClick={submitForm} type="submit">Өзгерту</button>
        </div>

        </div>
              </div>
            </div>
             
          </div>
        </div>
        </div> 
        </>
    );
}
export default TeacherChangePassword;
