import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import TeacherSidebar from "./TeacherSidebar";


const baseUrl='http://127.0.0.1:8000/api';

function TeacherSearch(){
    const [courseData, setCourseData] = useState([]);
    const {searchstring} = useParams();

   

   useEffect(()=>{
    try{
        axios.get(baseUrl + '/teacher-search-courses/'+ searchstring).then((res)=>{
          setCourseData(res.data);
        });
      }catch(error){
        console.log(error);
      }
}, []);


    return(
        <>

<div id="back-img">
        <div class="container mt-4" >
          <div class="row">
            <aside class="col md-3">
              <TeacherSidebar/>
            </aside>
            <div class="col-9">
              <div class="card">
                 <h4 id="add-word">Курс қосу</h4>
                 <div class="card-body" id="course-body">

        <div className="row mb-4">
         {courseData.map((course, index) => 
          <div className="col-md-4 mb-3">
           <div className="card" id="my-courses-cards">
            <img src={course.featured_img} className="card-img-top" alt="..."></img>
            <div className="card-body">
             <h5 className="card-title"><Link to={'/teacher-all-chapters/'+ course.id}>{course.title}</Link></h5>
             <p>Enrolled: <Link to={'/teacher-enrolled-students/'+ course.id} className="card-text">{course.total_enrolled_students}</Link></p>
            </div>
           </div>
         </div>
         )}
        </div>
        
        </div>
              </div>
            </div> 
          </div>
        </div>
        </div> 
 
 
 
        </>
    );
}
export default TeacherSearch;
