import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import './TeacherMyCourses.css';
import {
    MDBCard,
    MDBCardImage,
    MDBCardBody,
    MDBCardTitle,
    MDBCardText,
    MDBCardGroup
  } from 'mdb-react-ui-kit';

const baseUrl='http://127.0.0.1:8000/api';

function TeacherMyCourses() {

    useEffect(()=>{
        document.title='My Courses';
    });

    const [courseData, setCourseData] = useState([]);

    const teacherId=localStorage.getItem('teacherId');
    

    useEffect(()=>{
        try{
            axios.get(baseUrl + '/teacher-courses/'+ teacherId).then((res)=>{
                setCourseData(res.data);
            });
          }catch(error){
            console.log(error);
          }
    }, []);

    const styling={
        
        width: "20rem"
     }


    return (
        <>
        
        <TeacherHeader />

        <MDBCardGroup>
        {courseData.map((course, index) => 
      <MDBCard >
        <MDBCardImage src={course.featured_img} alt='...' position='top' style={styling}/>
        <MDBCardBody>
          <MDBCardTitle><Link to={'/teacher-all-chapters/'+ course.id}>{course.title}</Link></MDBCardTitle>
          <MDBCardText>
           Total 123
          </MDBCardText>
          <button>Delete</button>
      <Link to={'/teacher-add-chapter/'+ course.id}><button >Add Chapter</button></Link>
      <Link to={'/teacher-edit-course/'+ course.id}><button >Edit</button></Link>
        </MDBCardBody>
      </MDBCard>
     )}
      
    </MDBCardGroup>

        <div class="card-group" style={styling}>
  <div class="card">
  {courseData.map((course, index) => 
    <div class="card-body">
      <img src={course.featured_img} className="card-img-top" alt="..."></img>
      <h5 class="card-title"><Link to={'/teacher-all-chapters/'+ course.id}>{course.title}</Link></h5>
      <p class="card-text">Total 123</p>
      <button>Delete</button>
      <Link to={'/teacher-add-chapter/'+ course.id}><button >Add Chapter</button></Link>
      <Link to={'/teacher-edit-course/'+ course.id}><button >Edit</button></Link>
    </div>
  )}
  </div>
 
 
</div>

   
       
        </>
    );
}
export default TeacherMyCourses;
