import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import Swal from "sweetalert2";

const baseUrl='http://127.0.0.1:8000/api';

function TeacherProfileSettings() {
    useEffect(()=>{
        document.title='Profile Settings';
      });

      const teacherId=localStorage.getItem('teacherId');

      const [teacherData, setteacherData] = useState({
        'full_name': '',
        'email': '',
        'qualification': '',
        'mobile_no': '',
        'skills': '',
        'profile_fimg': '',
        'p_img': '',
        'status': '',
        'facebook_url': '',
        'twitter_url': '',
        'instagram_url': '',
        'website_url': '',
      });

     
    useEffect(()=>{

          //fetch current teacher data
          try{
            axios.get(baseUrl + '/teacher/'+ teacherId).then((res)=>{
                setteacherData(res.data);
            });
          }catch(error){
            console.log(error);
          }
          //End

      
    }, []);

        //Change Element Value
  const handleChange=(event)=>{
    setteacherData({
      ...teacherData,
      [event.target.name]: event.target.value
    });
  }

  const handleFileChange=(event)=>{
    setteacherData({
      ...teacherData,
      [event.target.name]: event.target.files[0]
    });
  }

  const submitForm=()=>{
    const teacherFormData = new FormData();
    teacherFormData.append("full_name", teacherData.full_name)
    teacherFormData.append("email", teacherData.email)
    teacherFormData.append("qualification", teacherData.qualification)
    teacherFormData.append("mobile_no", teacherData.mobile_no)
    teacherFormData.append("skills", teacherData.skills)
    teacherFormData.append("facebook_url", teacherData.facebook_url)
    teacherFormData.append("twitter_url", teacherData.twitter_url)
    teacherFormData.append("instagram_url", teacherData.instagram_url)
    teacherFormData.append("website_url", teacherData.website_url)

    if(teacherData.p_img !==''){
        teacherFormData.append('profile_img', teacherData.p_img, teacherData.p_img.name)
    }

    try{
      axios.put(baseUrl + '/teacher/'+ teacherId + '/', teacherFormData, {
        headers: {
            'content-type': 'multipart/form-data'
        }
      }).then((response)=>{
        if(response.status==200){
            Swal.fire({
                title: 'Data updated',
                icon: "success",
                timer: 5000,
                timerProgressBar: true,
                showConfirmButton: false
            });
        }
      });
    }catch(error){
        console.log(error);
        setteacherData({'status': 'error'});
    }

    

  };

  const teacherLoginStatus=localStorage.getItem('teacherLoginStatus')
  if(teacherLoginStatus!=='true'){
    window.location.href='/teacher-login';
  }
    return (
        <>
        <TeacherHeader />
        Profile settings
        <form>
           <div className="form-group">
             <input name="full_name" value={teacherData.full_name} onChange={handleChange} type="text" className="form-control" id="InputUsername1"  placeholder="Аты - жөні"></input>
           </div>

           <div className="form-group">
             <input name="email" value={teacherData.email} onChange={handleChange} type="email" className="form-control" id="InputEmail1"  placeholder="Электрондық пошта"></input>
           </div>

           <div className="form-group">
               <input name="p_img" onChange={handleFileChange} type="file" className="form-control" id="inputGroupFile03" aria-label="Upload" ></input>
               {teacherData.profile_img &&
                <img src={teacherData.profile_img} alt="opps!" width="100" height="100" />
               }
           </div>

           <div className="form-group">
             <input name="qualification" value={teacherData.qualification} onChange={handleChange} type="text" className="form-control" id="InputQualification1"  placeholder="Біліктілік"></input>
           </div>

           <div className="form-group">
             <input name="mobile_no" value={teacherData.mobile_no} onChange={handleChange} type="number" className="form-control" id="InputNumber1"  placeholder="Тел. нөмірі"></input>
           </div>

           <div className="form-group">
             <textarea name="skills" value={teacherData.skills} onChange={handleChange} type="text" className="form-control" id="InputSkills" placeholder="Қабілеттілік пен дағдылар"></textarea>
           </div>

           <h6 style={{textAlign: 'center', marginTop: '50px'}}>Social accounts</h6>

           <div className="form-group">
             <input name="facebook_url" value={teacherData.facebook_url} onChange={handleChange} type="text" className="form-control" id="InputUsername1"  placeholder="Аты - жөні"></input>
           </div>

           <div className="form-group">
             <input name="twitter_url" value={teacherData.twitter_url} onChange={handleChange} type="text" className="form-control" id="InputUsername1"  placeholder="Аты - жөні"></input>
           </div>

           <div className="form-group">
             <input name="instagram_url" value={teacherData.instagram_url} onChange={handleChange} type="text" className="form-control" id="InputUsername1"  placeholder="Аты - жөні"></input>
           </div>

           <div className="form-group">
             <input name="website_url" value={teacherData.website_url} onChange={handleChange} type="text" className="form-control" id="InputUsername1"  placeholder="Аты - жөні"></input>
           </div>
           
           <div className="form-group">
             <button onClick={submitForm} type="submit">Update</button>
           </div>

         </form>
        </>
    );
}
export default TeacherProfileSettings;
