import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import Swal from "sweetalert2";


const baseUrl='http://127.0.0.1:8000/api';

function AssignmentDetail(){
    const [assignmentData, setassignmentData] = useState([])
    let {assignment_id}=useParams();
    const [workData, setworkData] = useState({
        upload: ''
      });

      const handleFileChange=(event)=>{
        window.URL=window.URL || window.webkitURL;
        var upload = document.createElement('upload');
        upload.src = URL.createObjectURL(event.target.files[0])

        setworkData({
          ...workData,
          [event.target.name]: event.target.files[0]
        });
      }

      const userId=localStorage.getItem('userId');
      const teacherId=localStorage.getItem('teacherId');

      const submitForm=()=>{
        const _formData = new FormData();
        _formData.append("teacher", teacherId)
        _formData.append("user", userId)
        _formData.append('upload', workData.upload, workData.upload.name)
    
        try{
          axios.post(baseUrl+'/works/', _formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
          }).then((res)=>{
            //console.log(res.data);
            if(res.status===200 || res.status===201){
              Swal.fire({
                  title: 'Data added',
                  icon: "success",
                  timer: 3000,
                  timerProgressBar: true,
                  showConfirmButton: false
              });
            }
          });
        }catch(error){
            console.log(error);
            
        }
    
        
    
      };

    useEffect(()=>{
        try{
            axios.get(baseUrl + '/assignmentd/'+ assignment_id).then((res)=>{
                setassignmentData(res.data);
                
            });
          }catch(error){
            console.log(error);
          }
    
    
    }, []);
    return(
        <>
        <div id="back-img">
    <div className="card" id="assign-det">
        <div class="container mt-3" >
          <div class="row">
            <div class="col-10">
                <h4 id="det-word" className="assign-c">{assignmentData.title}</h4>
                <h5 id="assign">{assignmentData.detail}</h5>
                <h5 id="assign"><Link to={assignmentData.upload} id="a">Материал</Link></h5>
                
                <form>

           <div className="form-group">
               <input name="upload" type="file" onChange={handleFileChange} className="form-control" id="inputGroupFile03" aria-label="Upload" ></input>
           </div>
           
           <div className="form-group">
             <button type="button" id="login-button" onClick={submitForm} >Жүктеу</button>
           </div>

         </form>
            </div> 
          </div>
        </div>
        </div>
        </div>
        </>
    );
}
export default AssignmentDetail;
