import {Link} from 'react-router-dom';
import {useEffect, useState} from 'react';
import axios from 'axios';

const baseUrl='http://127.0.0.1:8000/api';
function UserLogin() {

  useEffect(()=>{
    document.title='User Log In';
  });

  const [userLoginData, setuserLoginData] = useState({
    email: '',
    password: ''
  });

  const handleChange=(event)=>{
    setuserLoginData({
      ...userLoginData,
      [event.target.name]: event.target.value
    });
  }

  const submitForm=()=>{
    const userFormData = new FormData();
    userFormData.append('email', userLoginData.email)
    userFormData.append('password', userLoginData.password)

    try{
      axios.post(baseUrl + '/user-login', userFormData).then((res)=>{
        if(res.data.bool===true){
          localStorage.setItem('userLoginStatus', true);
          localStorage.setItem('userId', res.data.user_id);
          window.location.href='/user-profile';
        }
      });
    }catch(error){
      console.log(error);
    }


  };

  const userLoginStatus=localStorage.getItem('userLoginStatus')
  if(userLoginStatus==='true'){
    window.location.href='/user-profile';
  }

  return (
    <>
       {/* Left Part */}
       <div className="leftPart">
         <Link to="/"><p className="logo" >lulu school</p></Link>
         <div id = "register-info">
          <h3 id="header-w">Аккаунтқа кіру</h3>
    

            <div className="form-group">
             <input name="email" value={userLoginData.email} onChange={handleChange} type="email" className="form-control" id="InputEmail1" aria-describedby="emailHelp" placeholder="Электрондық пошта"></input>
           </div>

            <div className="form-group">
              <input name="password" type="password" value={userLoginData.password} onChange={handleChange} className="form-control" id="InputPassword1" placeholder="Құпия сөз"></input>
            </div>

            <div className="form-group">
              <button id="login-button" onClick={submitForm} type="submit">Кіру</button>
            </div>

            <div className="form-group" id="word-in-bottom">
               <p>Аккаунтыңыз жоқ па? <Link to="/user-signup" id="a">Тіркелу</Link></p>
            </div>

          
         </div>
       </div>
       
       
       {/* Right Part */}
       <div className="rightPart"></div>
    </>
  );
}

export default UserLogin;
