import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import './TeacherMyCourses.css';

const baseUrl='http://127.0.0.1:8000/api';

function EnrolledStudents() {

    useEffect(()=>{
        document.title='Enrolled Students';
    });

    const [studentData, setstudentData] = useState([]);

   let {course_id}=useParams();
   

   useEffect(()=>{
    try{
        axios.get(baseUrl + '/fetch_enrolled_users/'+ course_id).then((res)=>{
          setstudentData(res.data);
        });
      }catch(error){
        console.log(error);
      }
}, []);
    


    return (
        <>
   <h1>Enrolled Users</h1>
   <Link to="/teacher-add-assignment" className="btn btn-success">Add Assignment</Link>
<table className="table">
  <thead>
    <tr>
      <th scope="col">Full name</th>
      <th scope="col">Email</th>
      <th scope="col">Username</th>
    </tr>
  </thead>
  <tbody>
  {studentData && studentData.map((row, index) => 
    <tr key={index}>
      <td>{row.user.full_name}</td>
      <td>{row.user.email}</td>
      <td>{row.user.username}</td>
    </tr>
  )}
</tbody>
</table>


     
        </>
    );
}
export default EnrolledStudents;
