import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import './TeacherMyCourses.css';
import TeacherSidebar from "./TeacherSidebar";

const baseUrl='http://127.0.0.1:8000/api';


function TeacherProfile() {
  useEffect(()=>{
    document.title='Профиль';
});
  
  const teacherId = localStorage.getItem("teacherId")
  const [teacherData, setTeacherData] = useState([])

  useEffect(() => {
    try {
        axios.get(baseUrl + '/teacher/' + teacherId + '/').then((res) => {
            setTeacherData(res.data)
        })
    } catch (error) {
        console.log(error)
    }
}, [])

if (!teacherData) {
    return <div>Loading...<Link to="/teacher-logout">Logout</Link></div>
}


    return (
        <>

       
<div id="back-img">
        <div className="container mt-4" >
          <div className="row">
            <aside className="col md-3">
              <TeacherSidebar/>
            </aside>
            <div className="col-9">
              <div className="card">
                 <h4 id="add-word">{teacherData.full_name}</h4>
                 <div className="card-body" id="profile-body">
    
        <div id="profile-el"><img src={teacherData.profile_img} id="person-img" className="card-img-top" alt="..."></img></div>

        <div id="profile-el">
    
<table id="profile-table" className="table table-bordered">
                <thead>
                    <tr>
                        <th scope="col">Пошта</th>
                        <td>{teacherData.email}</td>
                    </tr>
                    <tr>
                        <th scope="col">Біліктілігі</th>
                        <td>{teacherData.qualification}</td>
                    </tr>
                    <tr>
                        <th scope="col">Тел. нөмірі</th>
                        <td>{teacherData.mobile_no}</td>
                    </tr>
                    <tr>
                        <th scope="col">Қабілеттері</th>
                        <td>{teacherData.skills}</td>
                    </tr>
                </thead>
            </table>
    
        </div>
                </div>
              </div>
            </div> 
          </div>
        </div>
        </div>

        
        
        </>
    );
}
export default TeacherProfile;
