import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import Swal from "sweetalert2";

const baseUrl='http://127.0.0.1:8000/api';

function EditAssignment() {

    useEffect(()=>{
        document.title='Edit Assignment';
      });

      const [assignmentData, setassignmentData] = useState({
         course:'',
         title: '',
         detail: '',
         upload: ''
       });

       const handleChange=(event)=>{
         setassignmentData({
           ...assignmentData,
           [event.target.name]: event.target.value
         });
       }
 
     const handleFileChange=(event)=>{
         setassignmentData({
           ...assignmentData,
           [event.target.name]: event.target.files[0]
         });
       }
       
       const {assignment_id}=useParams();

       const submitForm=()=>{
         const _formData = new FormData();
         _formData.append('course', assignmentData.course)
         _formData.append('title', assignmentData.title)
         _formData.append('detail', assignmentData.detail)
         _formData.append('upload', assignmentData.upload, assignmentData.upload.name)
     
         try{
           axios.put(baseUrl+'/assignmentd/'+ assignment_id + '/', _formData, {
             headers: {
                 'Content-Type': 'multipart/form-data'
             }
           }).then((res)=>{
             console.log(res);
            // window.location.href='/teacher-add-chapter/5';
           });
         }catch(error){
             console.log(error);
             
         }
     
         
     
       };

       useEffect(()=>{
         try{
             axios.get(baseUrl + '/assignmentd/'+ assignment_id).then((res)=>{
                 setassignmentData(res.data);
             });
           }catch(error){
             console.log(error);
           }
     }, []);

    return (
        <>
        <div id="background1">
        <TeacherHeader />
        <div className="container mt-4" >
        <div id="my"><h4>Assignment өзгеру</h4></div>
        <form>
        <div className="form-group">
             <input name="title" value={assignmentData.title} onChange={handleChange} type="text" className="form-control" id="InputTitle" placeholder="Assignment атауы"></input>
           </div>

           <div className="form-group">
             <textarea name="detail" value={assignmentData.detail} onChange={handleChange} className="form-control" id="InputDescription" placeholder="Assignment сипаттамасы"></textarea>
           </div>

           <div className="form-group" style={{paddingTop: '21px'}}>
               <input name="upload"  onChange={handleFileChange} type="file" className="form-control" id="inputGroupFile03" aria-label="Upload" ></input>
           </div>
           
           <div className="form-group">
             <button type="button" onClick={submitForm}>Жүктеу</button>
           </div>

         </form>
         </div>
         </div>
        </>
    );
}
export default EditAssignment;
