import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import Swal from "sweetalert2";

const baseUrl='http://127.0.0.1:8000/api';

function AddChapter() {

    useEffect(()=>{
        document.title='Add Chapter';
      });
      useEffect(()=>{

          try{
            axios.get(baseUrl + '/course/' + course_id).then((res)=>{
                setCourseData(res.data);
                
            });
          }catch(error){
            console.log(error);
          }
    }, []);

    const [courseData, setCourseData] = useState([]); 
    const [chapterData, setChapterData] = useState({
        title: '',
        description: '',
        upload: '',
        remarks: ''
      });
    
    const handleChange=(event)=>{
        setChapterData({
          ...chapterData,
          [event.target.name]: event.target.value
        });
      }

      const handleFileChange=(event)=>{
        window.URL=window.URL || window.webkitURL;
        var upload = document.createElement('upload');
        upload.src = URL.createObjectURL(event.target.files[0])

        setChapterData({
          ...chapterData,
          [event.target.name]: event.target.files[0]
        });
      }

      const {course_id}=useParams();

      const submitForm=()=>{
        const _formData = new FormData();
        _formData.append('course', course_id)
        _formData.append('title', chapterData.title)
        _formData.append('description', chapterData.description)
        _formData.append('upload', chapterData.upload, chapterData.upload.name)
        _formData.append('remarks', chapterData.remarks)
    
        try{
          axios.post(baseUrl+'/chapter/', _formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
          }).then((res)=>{
            //console.log(res.data);
            if(res.status===200 || res.status===201){
              Swal.fire({
                  title: 'Data added',
                  icon: "success",
                  timer: 3000,
                  timerProgressBar: true,
                  showConfirmButton: false
              });
              window.location.href='/teacher-add-chapter/'+course_id;
            }
          });
        }catch(error){
            console.log(error);
            
        }
    
        
    
      };

    return (
        <>
      <div id="background1">
       <TeacherHeader />
        <div className="container mt-4" >
         <div id="my"><h4>Сабақ қосу to {courseData.title}</h4></div>
         <form>
           
           <div className="form-group">
             <input name="title" onChange={handleChange} type="text" className="form-control" id="InputTitle" placeholder="Бөлімнің атауы"></input>
           </div>

           <div className="form-group">
             <textarea name="description" onChange={handleChange} className="form-control" id="InputDescription" placeholder="Бөлімнің сипаттамасы"></textarea>
           </div>

           <div className="form-group" style={{paddingTop: '21px'}}>
               <input name="upload" onChange={handleFileChange} type="file" className="form-control" id="inputGroupFile03" aria-label="Upload" ></input>
           </div>

           <div className="form-group">
             <textarea name="remarks" onChange={handleChange} className="form-control" id="InputTechnologies" placeholder="Ескертулер"></textarea>
           </div>
           
           <div className="form-group">
             <button type="button" onClick={submitForm}>Жүктеу</button>
           </div>

         </form>
        </div>
      </div>
        </>
    );
}
export default AddChapter;
