import { useState, useEffect  } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import UserSidebar from "./User/UserSidebar";

const baseUrl='http://127.0.0.1:8000/api';

function AvailableCourses(){

    useEffect(()=>{
        document.title='Available Courses';
    });

    const [courseData, setCourseData] = useState([]);
    const [searchString, setSearchString] = useState({
      'search': ''
    });

    useEffect(()=>{
      try{
          axios.get(baseUrl + '/course').then((res)=>{
              setCourseData(res.data);
          });
        }catch(error){
          console.log(error);
        }
  }, []);

      //Change Element Value
const handleChange=(event)=>{
  console.log(searchString)
  setSearchString({
    ...searchString,
    [event.target.name]: event.target.value
    
  });
}

const searchCourse = () =>{
  window.location.href='/general-search/'+ searchString.search
}

    return(
      <>

        <div id="back-img">
        <div class="container mt-4" >
          <div class="row">
            <aside class="col md-3">
              <UserSidebar/>
            </aside>
            <div class="col-9">
              <div class="card">
                 <h4 id="add-word">Барлық мұғалімдер</h4>


        <nav className="navbar navbar-light" id="search-section">
         <div className="container-fluid">
          <form className="d-flex">
           <input name="search" id="search-input" onChange={handleChange} className="form-control me-2" type="search" placeholder="Search by title" aria-label="Search"/>
           <button onClick={searchCourse} id="search-button" type="button"><i class="bi bi-search"></i></button>
          </form>
         </div>
        </nav>



                 <div class="card-body" id="course-body"></div>

        <div className="row mb-4">
            {courseData.map((course, index) => 
          <div className="col-md-5 mb-3">
           <div className="card mb-6" id="my-courses-cards">
            <img src={course.featured_img} className="card-img-top" alt="..." height='170'></img>
            <div className="card-body">
            <Link to={'/course_detail/'+ course.id}><h5 className="card-title">{course.title}</h5></Link>
             <p className="card-text">Үйренушілер саны: {course.total_enrolled_students}</p>
             
            </div>
           </div>
         </div>
           )}

     </div>
        </div>
      </div>
    </div>
  </div>
</div>
      </>
    );
}
export default AvailableCourses
