import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import Swal from "sweetalert2";


const baseUrl='http://127.0.0.1:8000/api';

function AssignmentDetail(){
    const [assignmentData, setassignmentData] = useState([])
    let {assignment_id}=useParams();

    useEffect(()=>{
        try{
            axios.get(baseUrl + '/assignmentd/'+ assignment_id).then((res)=>{
                setassignmentData(res.data);
                
            });
          }catch(error){
            console.log(error);
          }
    
    
    }, []);
    return(
        <>
        <div id="back-img">
    <div className="card" id="user-det">
        <div class="container mt-3" >
          <div class="row">
            <div class="col-6" id="user-info">
                <h4 id="det-word">{assignmentData.title}</h4>
                <h6><b>Пошта:</b> {assignmentData.detail}</h6>
                <h6><b>Қолданушы аты:</b><Link to={assignmentData.upload} id="a">Материал</Link></h6>
                <form>

           <div className="form-group" style={{paddingTop: '21px'}}>
               <input name="upload" type="file" className="form-control" id="inputGroupFile03" aria-label="Upload" ></input>
           </div>
           
           <div className="form-group">
             <button type="button" id="login-button" >Жүктеу</button>
           </div>

         </form>
            </div> 
          </div>
        </div>
        </div>
        </div>
        </>
    );
}
export default AssignmentDetail;
