import { useState, useEffect  } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import './TeacherMyCourses.css';
import Swal from "sweetalert2";

const baseUrl='http://127.0.0.1:8000/api';

function CheckQuizInCourse(props){

    useEffect(()=>{
        document.title='Тест бекіту';
    });

    const [quizData, setquizData] = useState([]);
    const teacherId=localStorage.getItem('teacherId');
    

    useEffect(()=>{
        try{
            axios.get(`${baseUrl}/fetch-quiz-assign-status/${props.quiz}/${props.course}`).then((res)=>{
                setquizData(res.data);
            });
          }catch(error){
            console.log(error);
          }

    }, []);


    const assignQuiz=(quiz_id) => {
        const _formData = new FormData();
        _formData.append("teacher", teacherId)
        _formData.append("course", props.course)
        _formData.append("quiz", props.quiz)
    
        try{
          axios.post(baseUrl+'/quiz-assign-course/', _formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
          }).then((res)=>{
            if(res.status===200||res.status===201){
                window.location.reload();
            }
          });
        }catch(error){
            console.log(error);
            
        }
    } 

    return(
        <>

        <div>
    
             {quizData.bool === false &&
             <button id="assign-button" onClick={()=>assignQuiz(props.quiz)}><i className="bi bi-paperclip"></i></button>
            }
            {quizData.bool === true&&
            <>
             <span id="span-word">Бекітілген</span>
             <Link to={'/attempted-users/'+ props.quiz} id="a">Тапсырған студенттер</Link>
             </>
            }
              
     </div>  
 


      
        </>
    );
}
export default CheckQuizInCourse;
