import { useState, useEffect  } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import Swal from "sweetalert2";
import TeacherSidebar from "./TeacherSidebar";

const baseUrl='http://127.0.0.1:8000/api';

function EditCourse() {

    useEffect(()=>{
        document.title='Edit Course';
      });
    const teacherId=localStorage.getItem('teacherId');
    const [cats, setCats]=useState([]);
    const [courseData, setCourseData] = useState({
        category: '',
        title: '',
        description: '',
        prev_fimg: '',
        f_img: '',
        technologies: ''
      });

    const {course_id}=useParams();  
    useEffect(()=>{
        try{
            axios.get(baseUrl + '/category').then((res)=>{
                setCats(res.data);
            });
          }catch(error){
            console.log(error);
          }

          //fetch current course data
          try{
            axios.get(baseUrl + '/teacher-course-detail/'+course_id+'/').then((res)=>{
                setCourseData({
                    category: res.data.category,
                    title: res.data.title,
                    description: res.data.description,
                    prev_fimg: res.data.featured_img,
                    f_img: '',
                    technologies: res.data.technologies

                });
            });
          }catch(error){
            console.log(error);
          }
          //End
         
          try{
            axios.get(baseUrl + '/course/' + course_id).then((res)=>{
                setCourseData(res.data);
                
            });
          }catch(error){
            console.log(error);
          }
      
    }, []);
    //console.log(cats);
    
    const handleChange=(event)=>{
        setCourseData({
          ...courseData,
          [event.target.name]: event.target.value
        });
      }

    const handleFileChange=(event)=>{
        setCourseData({
          ...courseData,
          [event.target.name]: event.target.files[0]
        });
      }

      const submitForm=()=>{
        const _formData = new FormData();
        _formData.append("category", courseData.category)
        _formData.append("teacher", teacherId)
        _formData.append("title", courseData.title)
        _formData.append("description", courseData.description)
        if(courseData.f_img!=''){
          _formData.append("featured_img", courseData.f_img, courseData.f_img.name)
        }
        _formData.append("technologies", courseData.technologies)
    
        try{
          axios.put(baseUrl+'/teacher-course-detail/'+course_id+'/', _formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
          }).then((res)=>{
            if(res.status == 200){
                Swal.fire({
                  title: 'updated',
                  icon: 'success',
                  toast: true,
                  timer: 3000,
                  position: 'top-right',
                  timerProgressBar: true,
                  showConfirmButton: false
                });
            }
          });
        }catch(error){
            console.log(error);
            
        }
    
        
    
      };

    return (
        <>
         <div id="back-img">
        <div class="container mt-4" >
          <div class="row">
            <aside class="col md-3">
              <TeacherSidebar/>
            </aside>
            <div class="col-9">
              <div class="card">
                 <h4 id="add-word">Курсты өзгеру - {courseData.title}</h4>
                 <div class="card-body">
        <form>
           <div className="form-group">
             <select name="category" value={courseData.category} onChange={handleChange} className="form-control" id="InputCategory" >
             <option>Курстың категориясын таңдаңыз</option>
              {cats.map((category, index)=>{return <option key={index} value={category.id}>{category.title}</option>})}
             </select>
           </div>

           <div className="form-group">
             <input name="title" value={courseData.title} onChange={handleChange} type="text" className="form-control" id="InputTitle" placeholder="Курстың атауы"></input>
           </div>

           <div className="form-group">
             <textarea name="description" value={courseData.description} onChange={handleChange} className="form-control" id="InputDescription" placeholder="Курстың сипаттамасы"></textarea>
           </div>

           <div className="form-group" style={{paddingTop: '21px'}} >
               <input name="f_img" onChange={handleFileChange} type="file" className="form-control" id="inputGroupFile03" aria-label="Upload" ></input>
               {(courseData.prev_fimg) &&
                <img src={courseData.prev_fimg.url} alt="opps!" width="300" />
               }
           </div>


           <div className="form-group">
             <textarea name="technologies" value={courseData.technologies} onChange={handleChange} className="form-control" id="InputTechnologies" placeholder="Технологиялар"></textarea>
           </div>
           
           <div className="form-group">
             <button type="button" id="login-button"  onClick={submitForm}>өзгеру</button>
           </div>

         </form>
         </div>
              </div>
            </div> 
          </div>
        </div>
        </div>
        </>
    );
}
export default EditCourse;
