import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import './TeacherMyCourses.css';

const baseUrl='http://127.0.0.1:8000/api';

function TeacherMyQuizzes(){

    useEffect(()=>{
        document.title='My Quizzes';
    });

    const [quizData, setquizData] = useState([]);

    const teacherId=localStorage.getItem('teacherId');
    

    useEffect(()=>{
        try{
            axios.get(baseUrl + '/teacher-quizzes/'+ teacherId).then((res)=>{
                setquizData(res.data);
            });
          }catch(error){
            console.log(error);
          }
    }, []);


    return(
        <>
        <TeacherHeader/>
        <Link to="/teacher-add-quiz">Add Quiz</Link>

        <div className="container mt-4" >
        <div id="my"><h4>Менің quizzes</h4></div>    
        <div className="row mb-4">
         {quizData.map((quiz, index) => 
          <div className="col-md-4 mb-3">
           <div className="card" id="my-courses-cards">
            <div className="card-body">
             <h5 className="card-title"><Link to={'#'}>{quiz.title}</Link></h5>
             <p>Enrolled: <Link to={'#'} className="card-text">Total 123</Link></p>
            </div>
           </div>
         </div>
         )}
        </div>
     </div>  
 


      
        </>
    );
}
export default TeacherMyQuizzes;
