import './TeacherSignup.css';
import {Link} from 'react-router-dom';
import {useEffect, useState} from 'react';
import axios from 'axios';

const baseUrl='http://127.0.0.1:8000/api/teacher/';
function TeacherSignup() {

  useEffect(()=>{
    document.title='Teacher Sign UP';
  });
  const [teacherData, setteacherData] = useState({
    'full_name': '',
    'email': '',
    'password': '',
    'qualification': '',
    'mobile_no': '',
    'skills': '',
    'profile_img':'',
    'status': '',
    'otp_digit':''
  });

  //Change Element Value
  const handleChange=(event)=>{
    setteacherData({
      ...teacherData,
      [event.target.name]: event.target.value
    });
  }
  //End

  //Submit Form
  const submitForm=()=>{
    const otp_digit=Math.floor(100000+Math.random()*900000);
    const teacherFormData = new FormData();
    teacherFormData.append("full_name", teacherData.full_name)
    teacherFormData.append("email", teacherData.email)
    teacherFormData.append("password", teacherData.password)
    teacherFormData.append("qualification", teacherData.qualification)
    teacherFormData.append("mobile_no", teacherData.mobile_no)
    teacherFormData.append("skills", teacherData.skills)

    const xhr = new XMLHttpRequest();
    xhr.open("GET", "https://t4.ftcdn.net/jpg/05/50/60/55/360_F_550605549_PaTP81pjaCsrNTnfUaYlUZ8wmPpQSHY8.jpg");
    xhr.responseType = "blob";
    xhr.onload = function () {
      // Convert the response to a File object
      const file = new File([xhr.response], "image.jpg", {
        type: xhr.response.type,
      });
  
      // Append the File object to the FormData object
      teacherFormData.append("profile_img", file);
      teacherFormData.append("otp_digit", otp_digit)
  
      // Submit the form data
      axios.post(baseUrl, teacherFormData).then((response)=>{
        //navigate('/teacher-verify/'+response.data.id)
        window.location.href='/teacher-verify/'+response.data.id;
        //setteacherData({
         // 'full_name': '',
         // 'email': '',
        //  'password': '',
        //  'qualification': '',
         // 'mobile_no': '',
         // 'skills': '',
         // 'status': 'success'
        //});
      });
    };
    xhr.send();
  
    try {
      // Catch errors here
    } catch (error) {
      console.log(error);
      setteacherData({status: 'error'});
    }
  

  };
  //End

  //Redirecting to the Profile
  //const teacherLoginStatus=localStorage.getItem('teacherLoginStatus')
  //if(teacherLoginStatus==='true'){
  //  window.location.href='/teacher-profile';
  //}

  return (
    <> 
        {/* Left Part */}
       <div className="leftSide"></div>
        
        {/* Right Part */}
       <div className="rightSide">
         {/*teacherData.status ==='success' && <p className="text-success">Сәтті тіркелдіңіз!</p>*/}
         {/*teacherData.status ==='error' && <p className="text-danger">Тіркелу мүмкін емес, тағы да байқап көріңіз!</p>*/}
         <Link to="/"><p className="logo" >lulu school</p></Link>
         <div id = "register-info">
         <h3>Аккаунтқа тіркелу</h3>
         <form>
           <div className="form-group">
             <input name="full_name" value={teacherData.full_name} onChange={handleChange} type="text" className="form-control" id="InputUsername1" aria-describedby="emailHelp" placeholder="Аты - жөні"></input>
           </div>

           <div className="form-group">
             <input name="email" value={teacherData.email} onChange={handleChange} type="email" className="form-control" id="InputEmail1" aria-describedby="emailHelp" placeholder="Электрондық пошта"></input>
           </div>

           <div className="form-group">
             <input name="password" value={teacherData.password} onChange={handleChange} type="password" className="form-control" id="InputPassword1" placeholder="Құпия сөз"></input>
           </div>

           <div className="form-group">
             <input name="qualification" value={teacherData.qualification} onChange={handleChange} type="text" className="form-control" id="InputQualification1" aria-describedby="emailHelp" placeholder="Біліктілік"></input>
           </div>

           <div className="form-group">
             <input name="mobile_no" value={teacherData.mobile_no} onChange={handleChange} type="number" className="form-control" id="InputNumber1" aria-describedby="emailHelp" placeholder="Тел. нөмірі"></input>
           </div>

           <div className="form-group">
             <textarea name="skills" value={teacherData.skills} onChange={handleChange} type="text" className="form-control" id="InputSkills" aria-describedby="emailHelp" placeholder="Қабілеттілік пен дағдылар"></textarea>
           </div>
           
           <div className="form-group">
             <button onClick={submitForm} type="submit">Тіркелу</button>
           </div>

         </form>
         <p id="bottom-words">Тіркеліп қойдыңыз ба? <Link to="/teacher-login" id="a">Кіру</Link></p>
         </div>
         </div>

    </>
  );
}

export default TeacherSignup;
