import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import './TeacherMyCourses.css';

const baseUrl='http://127.0.0.1:8000/api/teacher-courses/';

function TeacherMyCourses() {

    useEffect(()=>{
        document.title='My Courses';
    });

    const [courseData, setCourseData] = useState([]);
    const [nextUrl, setnextUrl] = useState();
    const [apiUrl, setapiUrl] = useState(baseUrl);
    const [previousUrl, setpreviousUrl] = useState();

    const teacherId=localStorage.getItem('teacherId');
    

    useEffect(()=>{
        try{
            axios.get(apiUrl + teacherId).then((res)=>{
                setCourseData(res.data.results);
            });
          }catch(error){
            console.log(error);
          }
    }, []);

    const paginationHandle=(url)=>{
      try{
        axios.get(url).then((res)=>{
            setCourseData(res.data.results);
        });
      }catch(error){
        console.log(error);
      }
    }


    return (
        <>
      <div id="background">
      <TeacherHeader />
    
      <div className="container mt-4" >
        <div id="my"><h4>Менің курстарым</h4></div>    
        <div className="row mb-4">
         {courseData.map((course, index) => 
          <div className="col-md-4 mb-3">
           <div className="card" id="my-courses-cards">
            <img src={course.featured_img} className="card-img-top" alt="..."></img>
            <div className="card-body">
             <h5 className="card-title"><Link to={'/teacher-all-chapters/'+ course.id}>{course.title}</Link></h5>
             <p>Enrolled: <Link to={'/teacher-enrolled-students/'+ course.id} className="card-text">{course.total_enrolled_students}</Link></p>
            </div>
           </div>
         </div>
         )}
        </div>
     </div>  
 
     <nav aria-label="Page navigation example mt-5">
  <ul class="pagination justify-content-center">
    <li class="page-item"><button className="page-link" onClick={()=>paginationHandle(previousUrl)}><i className="bi bi-arrow-left"></i></button></li>
    <li class="page-item"><button className="page-link" onClick={()=>paginationHandle(nextUrl)}><i className="bi bi-arrow-right"></i></button></li>
  </ul>
</nav>

     </div>  
        </>
    );
}
export default TeacherMyCourses;
