import { useState, useEffect  } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import Swal from "sweetalert2";
import UserSidebar from "./UserSidebar";

const baseUrl='http://127.0.0.1:8000/api';

function UserChangePassword() {
    useEffect(()=>{
        document.title='Change Password';
      });
    const userId=localStorage.getItem('userId');

    const [studentData, setStudentData] = useState({
      'password': ''
    });

          //Change Element Value
  const handleChange=(event)=>{
    setStudentData({
      ...studentData,
      [event.target.name]: event.target.value
    });
  }

  const submitForm=()=>{
    const userFormData = new FormData();
    userFormData.append("password", studentData.password)

    try{
      axios.post(baseUrl + '/user/change-password/'+ userId + '/', userFormData).then((response)=>{
        if(response.status==200){
            window.location.href='/user-logout';
        }else{
            alert('Some errors occured!');
        }
      });
    }catch(error){
        console.log(error);
        setStudentData({'status': 'error'});
    }

    

  };

  const userLoginStatus=localStorage.getItem('userLoginStatus')
  if(userLoginStatus!=='true'){
    window.location.href='/user-login';
  }

    return (
        <>
       <div id="back-img">
        <div class="container mt-4" >
          <div class="row">
            <aside class="col md-3">
              <UserSidebar/>
            </aside>
            <div class="col-9" id="pass-change">
              <div class="card">
                 <h4 id="add-word">Құпия сөзді өзгерту</h4>
                 <div class="card-body">
        <div className="form-group">
              <input name="password" type="text" value={studentData.password} onChange={handleChange} className="form-control" id="InputPassword1" placeholder="Құпия сөз"></input>
        </div>

        <div className="form-group">
              <button id="login-button" onClick={submitForm} type="submit">Өзгерту</button>
        </div>
        </div>
              </div>
            </div> 
          </div>
        </div>
        </div>
        </>
    );
}
export default UserChangePassword;
