import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";

const baseUrl='http://127.0.0.1:8000/api';

function TeacherAddCourse() {

    const [cats, setCats]=useState([]);
    const [courseData, setCourseData] = useState({
        category: '',
        title: '',
        description: '',
        f_img: '',
        technologies: ''
      });

    useEffect(()=>{
        try{
            axios.get(baseUrl + '/category').then((res)=>{
                setCats(res.data);
            });
          }catch(error){
            console.log(error);
          }
    }, []);
    
    const handleChange=(event)=>{
        setCourseData({
          ...courseData,
          [event.target.name]: event.target.value
        });
      }

    const handleFileChange=(event)=>{
        setCourseData({
          ...courseData,
          [event.target.name]: event.target.files[0]
        });
      }

      const submitForm=()=>{
        const _formData = new FormData();
        _formData.append("category", courseData.category)
        _formData.append("email", 1)
        _formData.append("password", courseData.title)
        _formData.append("qualification", courseData.description)
        _formData.append("mobile_no", courseData.f_img, courseData.f_img.name)
        _formData.append("technologies", courseData.technologies)
    
        try{
          axios.post(baseUrl+'/course/', _formData, {
            headers: {
                'content-type': 'multipart/form-data'
            }
          }).then((res)=>{
            console.log(res.data);
          });
        }catch(error){
            console.log(error);
            
        }
    
        
    
      };

    return (
        <>
        <TeacherHeader />
        
        <form>
           <div className="form-group">
             <select name="category" onChange={handleChange} type="text" className="form-control" id="InputCategory" >
              <option selected>Курстың категориясын таңдаңыз</option>
              {cats.map((category, index)=>{return <option key={index} value={category.id}>{category.title}</option>})}
             </select>
           </div>

           <div className="form-group">
             <input name="title" onChange={handleChange} type="text" className="form-control" id="InputTitle" placeholder="Курстың атауы"></input>
           </div>

           <div className="form-group">
             <textarea name="description" onChange={handleChange} type="text" className="form-control" id="InputDescription" placeholder="Курстың сипаттамасы"></textarea>
           </div>

           <div class="input-group mb-3" >
               <input name="f_img" onChange={handleFileChange} type="file" class="form-control" id="inputGroupFile03" aria-label="Upload" ></input>
           </div>

           <div className="form-group">
             <textarea name="technologies" onChange={handleChange} type="text" className="form-control" id="InputTechnologies" placeholder="Технологиялар"></textarea>
           </div>
           
           <div className="form-group">
             <button type="submit" onClick={submitForm}>Жүктеу</button>
           </div>

         </form>

        </>
    );
}
export default TeacherAddCourse;
