import { useState, useEffect  } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import './TeacherMyCourses.css';
import Swal from "sweetalert2";
import TeacherSidebar from "./TeacherSidebar";

const baseUrl='http://127.0.0.1:8000/api';

function TeacherMyQuizzes(){

    useEffect(()=>{
        document.title='Менің тесттерім';
    });

    const [quizData, setquizData] = useState([]);
    const [totalRes, settotalRes] = useState(0);
    const teacherId=localStorage.getItem('teacherId');
    

    useEffect(()=>{
        try{
            axios.get(baseUrl + '/teacher-quizzes/'+ teacherId).then((res)=>{
                setquizData(res.data);
            });
          }catch(error){
            console.log(error);
          }
    }, []);


    const handleDelete = (quiz_id) => {
        Swal.fire({
            title: 'Растау',
            text: "Тестті өшіретіндігіңізге сенімдісіз бе?",
            icon: 'info',
            iconColor: 'lightpink',
            confirmButtonText: 'ОК',
            showCancelButton: true
          }).then((result)=>{
            if(result.isConfirmed){
                try{
                    axios.delete(baseUrl + '/quiz/'+ quiz_id).then((res)=>{
                        Swal.fire('OK!', 'Тест өшірілді');
                        try{
                            axios.get(baseUrl + '/teacher-quizzes/'+ teacherId).then((res)=>{
                                settotalRes(res.data.length)
                                setquizData(res.data);
                            });
                          }catch(error){
                            console.log(error);
                          }
                    });
                  }catch(error){
                    Swal.fire('Қате', 'Тестті өшіру сәтсіз аяқталды');
                  }
            }else{
                Swal.fire('Қате', 'Тестті өшіру сәтсіз аяқталды');
            }
          });
    }
    

    return(
        <>
        <div id="back-img">
        <div className="container mt-4" >
          <div className="row">
            <aside className="col md-3">
              <TeacherSidebar/>
            </aside>
            <div className="col-9">
              <div className="card">
                 <h4 id="add-word">Тесттер тізімі</h4>
                 <Link to="/teacher-add-quiz" id="another-button">ТЕСТ ҚОСУ</Link>
        <div className="card-body">
<table className="table table-bordered pb-1">
  <tbody>
  {quizData.map((row, index)=> (
            <tr key={index}>
              <td>{index + 1}</td>
              <td><span className="card-title"><Link to={'/teacher-all-questions/'+ row.id} id="a">{row.title}</Link></span></td>
              <td><span><Link to={'/teacher-edit-quiz/' + row.id} className="card-text" id="a">Тестті өзгерту</Link></span></td>
              <td><button id="chat-button" onClick={()=>handleDelete(row.id)}><i className="bi bi-trash3-fill"></i></button></td>
              </tr>
          ))}
</tbody>
</table>
</div>
              </div>
            </div> 
          </div>
        </div>
        </div> 
      
        </>
    );
}
export default TeacherMyQuizzes;
