import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import Swal from "sweetalert2";


const baseUrl='http://127.0.0.1:8000/api';

function CourseDetail(){
    let {course_id}=useParams();
    const[userLoginStatus, setUserLoginStatus]=useState();
    const[enrollStatus, setEnrollStatus]=useState();
    const[favStatus, setFavStatus]=useState();
    const userId=localStorage.getItem('userId');
    const [courseData, setCourseData] = useState([]);
    const [chapterData, setchapterData] = useState([]);
    const [studyData, setstudyData] = useState([]);
    const [assignmentData, setassignmentData] = useState([])

    useEffect(()=>{
      try{
          axios.get(baseUrl + '/user/study-materials/'+ course_id).then((res)=>{
              setstudyData(res.data);
              
          });
        }catch(error){
          console.log(error);
        }
  }, []);

  useEffect(()=>{
    try{
        axios.get(baseUrl + '/user/chapters/'+ course_id).then((res)=>{
            setchapterData(res.data);
            
        });
      }catch(error){
        console.log(error);
      }
}, []);

  useEffect(()=>{
    try{
        axios.get(baseUrl + '/user/assignments/'+ course_id).then((res)=>{
            setassignmentData(res.data);
            
        });
      }catch(error){
        console.log(error);
      }

      try{
        axios.get(baseUrl + '/course/' + course_id).then((res)=>{
            setCourseData(res.data);
            
        });
      }catch(error){
        console.log(error);
      }

}, []);


    useEffect(()=>{
        //fetch enroll status
        try{
            axios.get(baseUrl + '/fetch_enroll_status/' + userId+'/'+course_id).then((res)=>{
                if(res.data.bool==true){
                    setEnrollStatus('success');
                }
                
            });
          }catch(error){
            console.log(error);
          }


        const userLoginStatus=localStorage.getItem('userLoginStatus')
        if(userLoginStatus==='true'){
          setUserLoginStatus('success');
        }
    }, []);


    const enrollCourse=() => {
        const _formData = new FormData();
        _formData.append("course", course_id)
        _formData.append("user", userId)
    
        try{
          axios.post(baseUrl+'/user-enroll-course/', _formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
          }).then((res)=>{
            if(res.status===200||res.status===201){
                Swal.fire({
                    title: 'Курсқа сәтті қосылдыңыз!',
                    icon: 'success',
                    toast: true,
                    timer: 10000,
                    position: "top-right",
                    timerProgressBar: true,
                    showConfirmButton: false
                });
                setEnrollStatus('success');
            }
          });
        }catch(error){
            console.log(error);
            
        }
    }

    const markAsFav=() => {
      const _formData = new FormData();
        _formData.append("course", course_id)
        _formData.append("user", userId)
        _formData.append("status", true)
    
        try{
          axios.post(baseUrl+'/user-add-favorite-course/', _formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
          }).then((res)=>{
            if(res.status===200||res.status===201){
                Swal.fire({
                    title: 'Fav!',
                    icon: 'success',
                    toast: true,
                    timer: 10000,
                    position: "top-right",
                    timerProgressBar: true,
                    showConfirmButton: false
                });
                setFavStatus('success');
            }
          });
        }catch(error){
            console.log(error);
            
        }
    }

    const removeFav=() => {
      const _formData = new FormData();
        _formData.append("course", course_id)
        _formData.append("user", userId)
        _formData.append("status", false)
    
        try{
          axios.post(baseUrl+'/user-remove-favorite-course/'+ course_id + '/' + userId, _formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
          }).then((res)=>{
            if(res.status===200||res.status===201){
                Swal.fire({
                    title: 'Not Fav!',
                    icon: 'success',
                    toast: true,
                    timer: 10000,
                    position: "top-right",
                    timerProgressBar: true,
                    showConfirmButton: false
                });
                setFavStatus('');
            }
          });
        }catch(error){
            console.log(error);
            
        }
    }

  

    return(
        <>
  <div id="back-img">
    <div className="card" id="user-det">
        <div class="container mt-3" >
          <div class="row">
            
           <h1 id="add-header-word"> {courseData.title}</h1><br></br>
           Rating: 4.5/5
           {enrollStatus ==='success' && userLoginStatus ==='success' &&
           <>
           <button type="button" className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal">
            Rating
          </button>


<div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div className="modal-dialog">
    <div className="modal-content">
      <div className="modal-header">
        <h1 className="modal-title fs-5" id="exampleModalLabel">Rate for {courseData.title}</h1>
        <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div className="modal-body">
        ...
      </div>
      <div className="modal-footer">
        <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        <button type="button" className="btn btn-primary">Save changes</button>
      </div>
    </div>
  </div>
</div>
</>
}
           {/*<p>Total enrolled: {courseData.total_enrolled_students}</p>*/}
           {enrollStatus ==='success' && userLoginStatus ==='success' &&
            <p><span>You are already enrolled in this course</span></p>
           }
           {userLoginStatus ==='success' && enrollStatus !=='success' &&
            <button onClick={enrollCourse} type='button' className="btn btn-success">Enroll</button>
           }
           {userLoginStatus ==='success' && favStatus !=='success' &&
            <button onClick={markAsFav} title="Add in your favorite course List!" type='button' className="btn btn-outline-danger"><i className="bi bi-heart-fill"></i></button>
           }
           {userLoginStatus ==='success' && favStatus ==='success' &&
            <button onClick={removeFav} title="Remove from your favorite course List!" type='button' className="btn btn-primary"><i className="bi bi-heart-fill"></i></button>
           }
           {userLoginStatus !=='success' && 
             <Link to='/user-login'>Please login to enroll in this course</Link>
           }

<br></br>        



          {enrollStatus ==='success' && userLoginStatus ==='success' &&
          <>
            <h4 id="add-word">Барлық сабақтар</h4>
            <table className="table table-bordered pb-1">
        <tbody>
        {chapterData.map((chapter, index) =>
                  <tr key={index}>
                    <td>{index + 1}</td>
                    <td><h5 className="card-title">{chapter.title}</h5></td>
                    <td><h5><Link to={chapter.upload} id="a">Материал</Link></h5></td>
                    <td><h5>{chapter.remarks}</h5></td>
                  
                    </tr>
                )}
      </tbody>
            </table>
            </>
           }

<hr id="hhrr"></hr>


 
 {enrollStatus ==='success' && userLoginStatus ==='success' &&
<div>
      <div class="container mt-5" >
        <div id="my"><h4>All study materials</h4></div>    
        <div class="row mb-4">
        {studyData.map((row, index) => 
          <div class="col-md-4 mb-3">
           <div class="card" id="my-courses-cards">
            <div class="card-body">
             <h5 class="card-title">{row.title}</h5>
             
               <Link to={row.upload}>Files</Link>
               <h5>{row.description}</h5>
               <h5>{row.remarks}</h5>
            </div>
           </div>
         </div>
         )}
        </div>
     </div>  
    </div> 
}

{enrollStatus ==='success' && userLoginStatus ==='success' &&
<div>
      <div class="container mt-5" >
        <div id="my"><h4>All assignments</h4></div>    
        <div class="row mb-4">
        {assignmentData.map((row, index) => 
          <div class="col-md-4 mb-3">
           <div class="card" id="my-courses-cards">
            <div class="card-body">
             <h5 class="card-title">{row.title}</h5>
               <Link to={row.upload}>Files</Link>
               <h5>{row.detail}</h5>
               
            </div>
           </div>
         </div>
         )}
        </div>
     </div>  
    </div> 
}


          </div>
        </div>
        </div>
        </div>
        </>
    );
}
export default CourseDetail;
