import { useState, useEffect  } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import TeacherSidebar from "./TeacherSidebar";

const baseUrl='http://127.0.0.1:8000/api';

function AddAssignment() {

    useEffect(()=>{
        document.title='Тапсырма қосу';
      });
      const [courseData, setCourseData] = useState([]); 
      useEffect(()=>{

          try{
            axios.get(baseUrl + '/course/' + course_id).then((res)=>{
                setCourseData(res.data);
                
            });
          }catch(error){
            console.log(error);
          }
    }, []);


    const [assignmentData, setassignmentData] = useState({
        title: '',
        detail: '',
        upload: '',
      });
    
    const handleChange=(event)=>{
        setassignmentData({
          ...assignmentData,
          [event.target.name]: event.target.value
        });
      }

      const handleFileChange=(event)=>{
        window.URL=window.URL || window.webkitURL;
        var upload = document.createElement('upload');
        upload.src = URL.createObjectURL(event.target.files[0])

        setassignmentData({
          ...assignmentData,
          [event.target.name]: event.target.files[0]
        });
      }


      const {course_id}=useParams();

      const submitForm=()=>{
        const _formData = new FormData();
        _formData.append('course', course_id)
        _formData.append('title', assignmentData.title)
        _formData.append('detail', assignmentData.detail)
        _formData.append('upload', assignmentData.upload, assignmentData.upload.name)
        
        try{
          axios.post(baseUrl+'/assignment/'+course_id, _formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
          }).then((res)=>{
            //console.log(res.data);
            window.location.href='/teacher-add-assignment/'+course_id;
          });
        }catch(error){
            console.log(error);
            
        }
    
        
    
      };

    return (
        <>
      <div id="back-img">
        <div class="container mt-4" >
          <div class="row">
            <aside class="col md-3">
              <TeacherSidebar/>
            </aside>
            <div class="col-9">
              <div class="card">
                 <h4 id="add-word">Тапсырма қосу - {courseData.title}</h4>
                 <div class="card-body">
         <form>
           
           <div className="form-group">
             <input name="title" onChange={handleChange} type="text" className="form-control" id="InputTitle" placeholder="Тапсырманың аты"></input>
           </div>

           <div className="form-group">
             <textarea name="detail" onChange={handleChange} className="form-control" id="InputDescription" placeholder="Тапсырманың сипаттамасы"></textarea>
           </div>

           <div className="form-group" style={{paddingTop: '21px'}}>
               <input name="upload" onChange={handleFileChange} type="file" className="form-control" id="inputGroupFile03" aria-label="Upload" ></input>
           </div>
           
           <div className="form-group">
             <button type="button" id="login-button" onClick={submitForm}>Жүктеу</button>
           </div>

         </form>
         </div>
              </div>
            </div> 
          </div>
        </div>
        </div>
        </>
    );
}
export default AddAssignment;
