import UserHeader from "./UserHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";


const baseUrl='http://127.0.0.1:8000/api';

function CourseQuizList(){
    useEffect(()=>{
        document.title='User My Courses';
    });
   

    const [courseData, setCourseData] = useState([]);

    const userId=localStorage.getItem('userId');
    console.log(userId)

   useEffect(()=>{
    try{
        axios.get(baseUrl + '/fetch_enrolled_courses/'+ userId).then((res)=>{
          setCourseData(res.data);
        });
      }catch(error){
        console.log(error);
      }
}, []);
    

    return(
        <>
        <UserHeader />
        
        <div className="container mt-4" >
        <div id="my"><h4>Quiz List</h4></div>    
        <div className="row mb-4">
      
          <div className="col-md-4 mb-3">
           <div className="card" id="my-courses-cards">
           
            <div className="card-body">
             <h5 className="card-title">Ch quiz</h5>
             <Link to={'/take_quiz/1'} className="btn btn-warning">Take Quiz</Link>
            </div>
           </div>
         </div>
       
        </div>
     </div>  
 
        </>
    );
}
export default CourseQuizList;
