import { useState, useEffect  } from "react";
import axios from "axios";
import {useParams } from "react-router-dom";
import TeacherSidebar from "./TeacherSidebar";

const baseUrl='http://127.0.0.1:8000/api';

function QuestionDetail(){
    let {question_id}=useParams();
    const [questionData, setquestionData] = useState([])

  useEffect(()=>{

      try{
        axios.get(baseUrl + '/questions/' + question_id).then((res)=>{
            setquestionData(res.data);
            
        });
      }catch(error){
        console.log(error);
      }

}, []);


    return(
        <>

<div id="back-img">
        <div class="container mt-4" >
          <div class="row">
            <aside class="col md-3">
              <TeacherSidebar/>
            </aside>
            <div class="col-9">
              <div class="card">
                 <h4 id="add-word">{questionData.question}</h4>
        <div class="card-body" id="question">
<table className="table table-bordered pb-1" id="ans-table">
<thead>
  <tr>
    <th>Option1</th>
    <td>{questionData.ans1}</td>
  </tr>
  <tr>
    <th>Option2</th>
    <td>{questionData.ans2}</td>
  </tr>
  <tr>
    <th>Option3</th>
    <td>{questionData.ans3}</td>
  </tr>
  <tr>
    <th>Option4</th>
    <td>{questionData.ans4}</td>
  </tr>
  <tr>
    <th>Right option</th>
    <td>{questionData.right_ans}</td>
  </tr>
</thead>
</table>
</div>
              </div>
            </div> 
          </div>
        </div>
        </div>
          

        </>
    );
}
export default QuestionDetail;
