import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import Swal from "sweetalert2";
import TeacherSidebar from "./TeacherSidebar";


const baseUrl='http://127.0.0.1:8000/api';

function AllQuestions(){

    useEffect(()=>{
        document.title='Барлық сұрақтар';
    });

    const [questionData, setquestionData] = useState([]);
    const [quizData, setquizData] = useState([])
    const [totalResult, settotalResult] = useState(0);
    const {quiz_id} = useParams();
    

    useEffect(()=>{
        try{
            axios.get(baseUrl + '/quiz-questions/'+ quiz_id).then((res)=>{
                setquestionData(res.data);
                settotalResult(res.data.length);
            });
          }catch(error){
            console.log(error);
          }

          try{
            axios.get(baseUrl + '/quiz/' + quiz_id).then((res)=>{
                setquizData(res.data);
                
            });
          }catch(error){
            console.log(error);
          }

    }, []);

    const handleDeleteClick = (question_id) => {
        Swal.fire({
            title: 'Растау',
            text: "Сұрақты өшіретініңізге сенімдісіз бе?",
            icon: 'info',
            iconColor: 'lightpink',
            confirmButtonText: 'ОК',
            showCancelButton: true
          }).then((result)=>{
            if(result.isConfirmed){
                try{
                    axios.delete(baseUrl + '/questions/'+ question_id).then((res)=>{
                        Swal.fire('success', 'Сұрақ өшірілді');
                        try{
                            axios.get(baseUrl + '/quiz-questions/'+ quiz_id).then((res)=>{
                                settotalResult(res.data.length)
                                setquestionData(res.data);
                            });
                          }catch(error){
                            console.log(error);
                          }
                    });
                  }catch(error){
                    Swal.fire('Қате!', 'Сұрақ өшіру сәтсіз аяқталды!');
                  }
            }else{
                Swal.fire('Қате!', 'Сұрақ өшіру сәтсіз аяқталды!');
            }
          });
    }
    
    

    return (
        <>

<div id="back-img">
        <div class="container mt-4" >
          <div class="row">
            <aside class="col md-3">
              <TeacherSidebar/>
            </aside>
            <div class="col-9">
              <div class="card">
                 <h4 id="add-word">{quizData.title}</h4>
                 <Link to={"/teacher-add-question/" + quiz_id} id="another-button">СҰРАҚ ҚОСУ</Link>
        <div class="card-body">
<table className="table table-bordered pb-1">
  <tbody>
  {questionData.map((row, index)=> (
            <tr key={index}>
              <td>{index + 1}</td>
              <td><Link id="a" to={"/question-detail/"+row.id}>{row.question}</Link></td>
              <td><button id="chat-button" onClick={()=>handleDeleteClick(row.id)}><i class="bi bi-trash3-fill"></i></button></td>
              </tr>
          ))}
</tbody>
</table>
</div>
              </div>
            </div> 
          </div>
        </div>
        </div>

        </>
    );
}
export default AllQuestions;
