import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import Swal from "sweetalert2";


const baseUrl='http://127.0.0.1:8000/api';

function CourseDetail(){
    let {course_id}=useParams();
    const[userLoginStatus, setUserLoginStatus]=useState();
    const[enrollStatus, setEnrollStatus]=useState();
    const[favStatus, setFavStatus]=useState();
    const userId=localStorage.getItem('userId');
    const [courseData, setCourseData] = useState([]);
    const [chapterData, setchapterData] = useState([]);
    const [studyData, setstudyData] = useState([]);
    const [assignmentData, setassignmentData] = useState([])

    useEffect(()=>{
      try{
          axios.get(baseUrl + '/user/study-materials/'+ course_id).then((res)=>{
              setstudyData(res.data);
              
          });
        }catch(error){
          console.log(error);
        }
  }, []);

  useEffect(()=>{
    try{
        axios.get(baseUrl + '/user/chapters/'+ course_id).then((res)=>{
            setchapterData(res.data);
            
        });
      }catch(error){
        console.log(error);
      }
}, []);

  useEffect(()=>{
    try{
        axios.get(baseUrl + '/user/assignments/'+ course_id).then((res)=>{
            setassignmentData(res.data);
            
        });
      }catch(error){
        console.log(error);
      }

      try{
        axios.get(baseUrl + '/course/' + course_id).then((res)=>{
            setCourseData(res.data);
            
        });
      }catch(error){
        console.log(error);
      }

}, []);


    useEffect(()=>{
        //fetch enroll status
        try{
            axios.get(baseUrl + '/fetch_enroll_status/' + userId+'/'+course_id).then((res)=>{
                if(res.data.bool==true){
                    setEnrollStatus('success');
                }
                
            });
          }catch(error){
            console.log(error);
          }


        const userLoginStatus=localStorage.getItem('userLoginStatus')
        if(userLoginStatus==='true'){
          setUserLoginStatus('success');
        }
    }, []);


    const enrollCourse=() => {
        const _formData = new FormData();
        _formData.append("course", course_id)
        _formData.append("user", userId)
    
        try{
          axios.post(baseUrl+'/user-enroll-course/', _formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
          }).then((res)=>{
            if(res.status===200||res.status===201){
                Swal.fire({
                    title: 'Курсқа сәтті қосылдыңыз!',
                    icon: 'success',
                    toast: true,
                    timer: 10000,
                    position: "top-right",
                    timerProgressBar: true,
                    showConfirmButton: false
                });
                setEnrollStatus('success');
            }
          });
        }catch(error){
            console.log(error);
            
        }
    }
  

    const markAsFav=() => {
      const _formData = new FormData();
        _formData.append("course", course_id)
        _formData.append("user", userId)
        _formData.append("status", true)
    
        try{
          axios.post(baseUrl+'/user-add-favorite-course/', _formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
          }).then((res)=>{
            if(res.status===200||res.status===201){
                Swal.fire({
                    title: 'Fav!',
                    icon: 'success',
                    toast: true,
                    timer: 10000,
                    position: "top-right",
                    timerProgressBar: true,
                    showConfirmButton: false
                });
                setFavStatus('success');
            }
          });
        }catch(error){
            console.log(error);
            
        }
    }

    const removeFav=() => {
      const _formData = new FormData();
        _formData.append("course", course_id)
        _formData.append("user", userId)
        _formData.append("status", false)
    
        try{
          axios.post(baseUrl+'/user-remove-favorite-course/'+ course_id + '/' + userId, _formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
          }).then((res)=>{
            if(res.status===200||res.status===201){
                Swal.fire({
                    title: 'Not Fav!',
                    icon: 'success',
                    toast: true,
                    timer: 10000,
                    position: "top-right",
                    timerProgressBar: true,
                    showConfirmButton: false
                });
                setFavStatus('');
            }
          });
        }catch(error){
            console.log(error);
            
        }
    }

  

    return(
        <>
  <div id="back-img">
    <div className="card" id="user-det">
    <div id="header-back">
    <div>
    <h1 class="detail-header" id="add-course-word"> {courseData.title}</h1>
    {userLoginStatus ==='success' && favStatus !=='success' && enrollStatus ==='success' &&
           <button class="detail-header" onClick={markAsFav} title="Сүйікті курстарыңыздың қатарына қосыңыз!" type='button' id="fav-button"><i className="bi bi-heart-fill"></i></button>
    }
    {userLoginStatus ==='success' && favStatus ==='success' && enrollStatus ==='success' &&
           <button class="detail-header" onClick={removeFav} title="Сүйікті курстарыңыздың қатарынан алып тастаңыз!" type='button' id="fav-button"><i className="bi bi-heartbreak-fill"></i></button>
    }
    </div>
    
          

          {/*<p>Total enrolled: {courseData.total_enrolled_students}</p>*/}
          {enrollStatus ==='success' && userLoginStatus ==='success' &&
           <p><span id="enroll-word">You are already enrolled in this course</span></p>
          }
          {userLoginStatus ==='success' && enrollStatus !=='success' &&
           <button onClick={enrollCourse} type='button' id="enroll-button"><i class="bi bi-plus-square-fill"></i></button>
          }
          
          {userLoginStatus !=='success' && 
            <Link to='/user-login'>Please login to enroll in this course</Link>
          }

<br></br>
    </div>

        <div class="container mt-3" >
          <div class="row">

          {enrollStatus ==='success' && userLoginStatus ==='success' &&
          <>
            <h4 id="add-word">Барлық сабақтар</h4>
            <table className="table table-bordered pb-1">
        <tbody>
        {chapterData.map((row, index) =>
                  <tr key={index}>
                    <td>{index + 1}</td>
                    <td><h5 className="card-title">{row.title}</h5></td>
                    <td><h5><Link to={row.upload} id="a">Материал</Link></h5></td>
                    <td><h5>{row.remarks}</h5></td>
                  
                    </tr>
                )}
      </tbody>
            </table>
            <hr id="hhrr"></hr>
            </>
           }



 
 {enrollStatus ==='success' && userLoginStatus ==='success' &&
  <>
  <h4 id="add-word">Барлық оқу материалдары</h4>
  <table className="table table-bordered pb-1">
<tbody>
{studyData.map((row, index) =>
        <tr key={index}>
          <td>{index + 1}</td>
          <td><h5 className="card-title">{row.title}</h5></td>
          <td><h5><Link to={row.upload} id="a">Материал</Link></h5></td>
          <td><h5>{row.description}</h5></td>
          <td><h5>{row.remarks}</h5></td>
        
          </tr>
      )}
</tbody>
  </table>
  <hr id="hhrr"></hr>
  </>
  }



{enrollStatus ==='success' && userLoginStatus ==='success' &&
<>
  <h4 id="add-word">Барлық тапсырмалар</h4>
  <table className="table table-bordered pb-1">
<tbody>
{assignmentData.map((row, index) =>
        <tr key={index}>
          <td>{index + 1}</td>
          <td><h5 className="card-title">{row.title}</h5></td>
          <td><h5><Link to={row.upload} id="a">Материал</Link></h5></td>
          <td><h5>{row.detail}</h5></td>
        
          </tr>
      )}
</tbody>
  </table>
  </>
  }

          </div>
        </div>
        </div>
        </div>
        </>
    );
}
export default CourseDetail;
