import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import './TeacherMyCourses.css';

const baseUrl='http://127.0.0.1:8000/api';

function EnrolledStudents() {

    useEffect(()=>{
        document.title='Enrolled Students';
    });

    const [studentData, setStudentData] = useState([]);

    let {course_id}=useParams();
    

    useEffect(()=>{
        try{
            axios.get(baseUrl + '/fetch_enrolled_users/'+ course_id).then((res)=>{
                setStudentData(res.data);
            });
          }catch(error){
            console.log(error);
          }
    }, []);


    return (
        <>
      <div id="background">
      
    
      <div className="container mt-4" >
        <div id="my"><h4>Студенттер</h4></div>    
        <div className="row mb-4">
         {studentData.map((student, index) => 
          <div className="col-md-4 mb-3">
           <div className="card" id="my-courses-cards">
            <div className="card-body">
             <h5 className="card-title"><Link to={'#'+ student.row.id}>{student.row.full_name}</Link></h5>
             <p className="card-text">{student.row.email}</p>
             <p className="card-text">{student.row.username}</p>
             <button>View</button>
            </div>
           </div>
         </div>
         )}
        </div>
     </div>  
 


     </div>  
        </>
    );
}
export default EnrolledStudents;
