import UserHeader from "./UserHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import Swal from "sweetalert2";

const baseUrl='http://127.0.0.1:8000/api';

function UserProfileSettings() {
    useEffect(()=>{
        document.title='Profile Settings';
      });

      const userId=localStorage.getItem('userId');

      const [userData, setuserData] = useState({
        'full_name': '',
        'email': '',
        'username': '',
        'interested_categories': '',
        'profile_fimg': '',
        'p_img': ''
      });

     
    useEffect(()=>{

          //fetch current teacher data
          try{
            axios.get(baseUrl + '/user/'+ userId).then((res)=>{
                setuserData({
                    full_name: res.full_name,
                    email: res.email,
                    username: res.username,
                    interested_categories: res.interested_categories,
                    profile_fimg: res.featured_img,
                    p_img: ''

                });
            });
          }catch(error){
            console.log(error);
          }
          //End

      
    }, []);

        //Change Element Value
  const handleChange=(event)=>{
    setuserData({
      ...userData,
      [event.target.name]: event.target.value
    });
  }

  const handleFileChange=(event)=>{
    setuserData({
      ...userData,
      [event.target.name]: event.target.files[0]
    });
  }

  const submitForm=()=>{
    const userFormData = new FormData();
    userFormData.append("full_name", userData.full_name)
    userFormData.append("email", userData.email)
    userFormData.append("username", userData.username)
    userFormData.append("interested_categories", userData.interested_categories)

    if(userData.p_img !==''){
        userFormData.append('profile_img', userData.p_img, userData.p_img.name)
    }

    try{
      axios.put(baseUrl + '/user/'+ userId + '/', userFormData, {
        headers: {
            'content-type': 'multipart/form-data'
        }
      }).then((response)=>{
        if(response.status==200){
            Swal.fire({
                title: 'Data updated',
                icon: "success",
                timer: 5000,
                timerProgressBar: true,
                showConfirmButton: false
            });
        }
      });
    }catch(error){
        console.log(error);
        setuserData({'status': 'error'});
    }

    

  };

  const userLoginStatus=localStorage.getItem('userLoginStatus')
  if(userLoginStatus!=='true'){
    window.location.href='/teacher-login';
  }
    return (
        <>
        <UserHeader />
        Profile settings
        <form>
        <div className="form-group">
             <input name="full_name" value={userData.full_name} onChange={handleChange} type="text" className="form-control" id="InputUsername1" aria-describedby="emailHelp" placeholder="Толық аты - жөні"></input>
           </div>

           <div className="form-group">
             <input name="email" value={userData.email} onChange={handleChange} type="email" className="form-control" id="InputEmail1" aria-describedby="emailHelp" placeholder="Электрондық пошта"></input>
           </div>

           <div className="form-group">
             <input name="username" value={userData.username} onChange={handleChange} type="text" className="form-control" id="Inputusername1" aria-describedby="emailHelp" placeholder="Қолданушы аты"></input>
           </div>

           <div className="form-group">
               <input name="p_img" onChange={handleFileChange} type="file" className="form-control" id="inputGroupFile03" aria-label="Upload" ></input>
               {userData.profile_img &&
                <img src={userData.profile_img.url} alt="opps!" width="300" />
               }
           </div>

           <div className="form-group">
             <textarea name="interested_categories" value={userData.interested_categories} onChange={handleChange} type="text" className="form-control" id="Inputinterest" aria-describedby="emailHelp" placeholder="Қызығушылығы"></textarea>
           </div>
           
           <div className="form-group">
             <button onClick={submitForm} type="submit">Update</button>
           </div>

           
         </form>
        </>
    );
}
export default UserProfileSettings;
