import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import './TeacherMyCourses.css';
import Swal from "sweetalert2";

const baseUrl='http://127.0.0.1:8000/api';

function CheckQuizInCourse(props){

    useEffect(()=>{
        document.title='Assign Quiz';
    });

    const [quizData, setquizData] = useState([]);
    const[assignStatus, setassignStatus]=useState();
    const [courseData, setCourseData] = useState([]); 
    const teacherId=localStorage.getItem('teacherId');
    const {course_id}=useParams();
    

    useEffect(()=>{
        try{
            axios.get(`${baseUrl}/fetch-quiz-assign-status/${props.quiz}/${props.course}`).then((res)=>{
                setquizData(res.data);
            });
          }catch(error){
            console.log(error);
          }

    }, []);


    const assignQuiz=(quiz_id) => {
        const _formData = new FormData();
        _formData.append("teacher", teacherId)
        _formData.append("course", props.course_id)
        _formData.append("quiz", props.quiz_id)
    
        try{
          axios.post(baseUrl+'/quiz-assign-course/', _formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
          }).then((res)=>{
            if(res.status===200||res.status===201){
                window.location.reload();
            }
          });
        }catch(error){
            console.log(error);
            
        }
    } 

    return(
        <>

        <div className="container mt-4" >
     
        <div className="row mb-4">
         
        
          <div className="col-md-4 mb-3">
           <div className="card" id="my-courses-cards">
            <div className="card-body">
    
             {quizData.bool == false &&
             <button onClick={()=>assignQuiz(props.quiz)}>Assign Quiz</button>
            }
            {quizData.bool == true&&
             <span className="btn btn-success">Assigned</span>
            }
            </div>
           </div>
         </div>
        
         
        </div>
     </div>  
 


      
        </>
    );
}
export default CheckQuizInCourse;
