import './TeacherLogin.css';
import {Link, useNavigate, useParams} from 'react-router-dom';
import {useEffect, useState} from 'react';
import axios from 'axios';

const baseUrl='http://127.0.0.1:8000/api';
function VerifyTeacher() {

  useEffect(()=>{
    document.title='Verify';
  });

  const [teacherData, setteacherData] = useState({
    otp_digit: ''
  });
  const [errorMSg, seterrorMsg] = useState([]);
  const {teacher_id}=useParams();
  const navigate=useNavigate()

  const handleChange=(event)=>{
    setteacherData({
      ...teacherData,
      [event.target.name]: event.target.value
    });
  }

  const submitForm=()=>{
    const teacherFormData = new FormData();
    teacherFormData.append('otp_digit', teacherData.otp_digit)

    try{
      axios.post(baseUrl + '/teacher-verify/' + teacher_id + '/', teacherFormData).then((res)=>{
        if(res.data.bool===true){
          localStorage.setItem('teacherLoginStatus', true);
          localStorage.setItem('teacherId', res.data.teacher_id);
          navigate('/teacher-profile');
        }else{
          seterrorMsg(res.data.msg);
        }
      });
    }catch(error){
      console.log(error);
    }


  };

  const teacherLoginStatus=localStorage.getItem('teacherLoginStatus')
  if(teacherLoginStatus==='true'){
    window.location.href='/teacher-profile';
 }

  return (
    <>
       <div className="leftPart">
         
         <div id = "register-info" className='verify-form'>
          <h3 id="header-digit">Поштаға келген 6 цифрлы санды енгізіңіз</h3>
          <form>
          {errorMSg && <p className='text-danger'>{errorMSg}</p>}
            <div className="form-group">
             <input name="otp_digit" value={teacherData.otp_digit} onChange={handleChange} type="number" className="form-control" id="InputEmail1" aria-describedby="emailHelp" placeholder="6 цифрлы сан"></input>
           </div>

            <div className="form-group">
              <button id="login-button" onClick={submitForm} type="submit">Растау</button>
            </div>

          </form>
         </div>
       </div>

       {/* Right Part */}
       <div className="rightPart"></div>
    </>
  );
}

export default VerifyTeacher;
