import { useState, useEffect  } from "react";
import axios from "axios";

const baseUrl='http://127.0.0.1:8000/api';

function MessageListUser(props){

    const [msgData, setmsgData] = useState([]);

    useEffect(()=>{
        try{
            axios.get(baseUrl + '/get-messages/'+props.teacher_id+'/'+props.user_id).then((res)=>{
                setmsgData(res.data);
            });
          }catch(error){
            console.log(error);
          }
    }, []);

    const fetchMsg=()=>{
      try{
        axios.get(baseUrl + '/get-messages/'+props.teacher_id+'/'+props.user_id).then((res)=>{
            setmsgData(res.data);
            const objDiv=document.getElementById("msgList");
            console.log(objDiv);
            objDiv.scrollTop = objDiv.scrollHeight;
        });
      }catch(error){
        console.log(error);
      }
    }
    const msgList={
      height: '500px',
      overflow: 'auto',
      scrollbarWidth: 'none'
 }
    

    return(
        <>
        <h4 className="ms-3" onClick={fetchMsg} title="Refresh" ><i className="bi bi-bootstrap-reboot"></i></h4>
        <div id="msgList" style={msgList}>
         {msgData.map((row, index) =>  
            <div className="row mb-4">
              {row.msg_from == 'student' && 
             <div className="col-5 offset-7">
              <div className="alert mb-1" id="message1">
                {row.msg_text}
              </div>
              <small className="text-muted">{row.msg_time}</small>
             </div>
             }
             {row.msg_from != 'student' && 
             <div className="col-5">
              <div className="alert mb-1" id="message2">
                {row.msg_text}
              </div>
              <small className="text-muted">{row.msg_time}</small>
             </div>
             }
            </div>
         )}
         </div>
        </>
    );
}
export default MessageListUser;
