import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import UserSidebar from "./UserSidebar";

const baseUrl='http://127.0.0.1:8000/api';

function Search(){
    useEffect(()=>{
        document.title='Іздеу...';
    });
   

    const [courseData, setCourseData] = useState([]);
    const {searchstring} = useParams();

   

   useEffect(()=>{
    try{
        axios.get(baseUrl + '/search-courses/'+ searchstring).then((res)=>{
          setCourseData(res.data);
        });
      }catch(error){
        console.log(error);
      }
}, []);


    return(
        <>
          <div id="back-img">
        <div class="container mt-4" >
          <div class="row">
            <aside class="col md-3">
              <UserSidebar/>
            </aside>
            <div class="col-9">
              <div class="card">
                 <h4 id="add-word">Менің курстарым</h4>
                 <div class="card-body">

    <div className="container mt-4" >   
        <div className="row mb-4">
        {courseData.length > 0 ? (
  courseData.map((row, index) => (
    <div className="col-md-4 mb-3">
      <div className="card" id="my-courses-cards">
        <div className="card-body">
          <Link to={'/course_detail/'+ row.id}>
            <h5 className="card-title">{row.title}</h5>
          </Link>
          <p>{row.teacher.full_name}</p>
          <Link to={'/user-quiz-list/'+ row.id} className="btn btn-warning">Quiz List</Link>
          <button>Unenroll</button>
        </div>
      </div>
    </div>
  ))
) : (
  <div>No courses found</div>
)}

        </div>
     </div>  

     </div>
              </div>
            </div> 
          </div>
        </div>
        </div> 
 
        </>
    );
}
export default Search;
