import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import './TeacherMyCourses.css';
import Swal from "sweetalert2";

const baseUrl='http://127.0.0.1:8000/api';

function MessageList(props){

    useEffect(()=>{
        document.title='Message List';
    });

    const [msgData, setmsgData] = useState([]);

    useEffect(()=>{
        try{
            axios.get(baseUrl + '/get-messages/'+props.teacher_id+'/'+props.user_id).then((res)=>{
                setmsgData(res.data);
            });
          }catch(error){
            console.log(error);
          }
    }, []);

    

    return(
        <>
        <span className="ms-5"><i className="bi bi-bootstrap-reboot"></i></span>Ñ‹
         {msgData.map((row, index) =>
            
            <div className="row mb-4">
             <div className="col-5 offset-7">
              <div className="alert alert-primary mb-1" >
                {row.msg_text}
              </div>
              <small className="text-muted">{row.msg_time}</small>
             </div>
            </div>
          
         )}
        </>
    );
}
export default MessageList;
