import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import Swal from "sweetalert2";

const baseUrl='http://127.0.0.1:8000/api';

function TeacherChangeForgotPassword() {
    useEffect(()=>{
        document.title='Change Forgot Password';
      });

    const [teacherData, setteacherData] = useState({
      'password': ''
    });
    const [successMsg, setsuccessMsg] = useState([]);
    const [errorMsg, seterrorMsg] = useState([]);
    const {teacher_id}=useParams()

          //Change Element Value
  const handleChange=(event)=>{
    setteacherData({
      ...teacherData,
      [event.target.name]: event.target.value
    });
  }

  const submitForm=()=>{
    const teacherFormData = new FormData();
    teacherFormData.append("password", teacherData.password)

    try{
      axios.post(baseUrl + '/teacher-change-forgot-password/'+teacher_id+'/', teacherFormData).then((response)=>{
        if(response.data.bool===true){
            setsuccessMsg(response.data.msg);
            seterrorMsg('');
        }else{
            seterrorMsg(response.data.msg);
            setsuccessMsg('');
        }
      });
    }catch(error){
        console.log(error);
        setteacherData({'status': 'error'});
    }  

  };

  const teacherLoginStatus=localStorage.getItem('teacherLoginStatus')
  if(teacherLoginStatus=='true'){
    window.location.href='/teacher-profile';
  }



    return (
        <>
        {/* Left Part */}
       <div className="leftSide"></div>
       
        Enter your password
        {successMsg && <p className='text-success'>{successMsg}</p>}
        {errorMsg && <p className='text-danger'>{errorMsg}</p>}
        <div className="form-group">
              <input name="password" type="password" value={teacherData.password} onChange={handleChange} className="form-control" id="InputPassword1" placeholder="password"></input>
        </div>

        <div className="form-group">
              <button id="login-button" onClick={submitForm} type="submit">Change</button>
        </div>
        </>
    );
}
export default TeacherChangeForgotPassword;
