import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const baseUrl='http://127.0.0.1:8000/api';

function AddChapter() {

    useEffect(()=>{
        document.title='Add Chapter';
      });

    const [chapterData, setChapterData] = useState({
        title: '',
        description: '',
        video: '',
        remarks: ''
      });
    
    const handleChange=(event)=>{
        setChapterData({
          ...chapterData,
          [event.target.name]: event.target.value
        });
      }

    const handleFileChange=(event)=>{
        setChapterData({
          ...chapterData,
          [event.target.name]: event.target.files[0]
        });
      }

      const submitForm=()=>{
        const _formData = new FormData();
        const {course_id}=useParams();
        _formData.append('course', course_id)
        _formData.append('title', chapterData.title)
        _formData.append('description', chapterData.description)
        _formData.append('video', chapterData.video, chapterData.video.name)
        _formData.append('remarks', chapterData.remarks)
    
        try{
          axios.post(baseUrl+'/chapter/', _formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
          }).then((res)=>{
            //console.log(res.data);
            window.location.href='/teacher-add-chapter/5';
          });
        }catch(error){
            console.log(error);
            
        }
    
        
    
      };

    return (
        <>
        <TeacherHeader />
        
        <form>
           
           <div className="form-group">
             <input name="title" onChange={handleChange} type="text" className="form-control" id="InputTitle" placeholder="Бөлімнің атауы"></input>
           </div>

           <div className="form-group">
             <textarea name="description" onChange={handleChange} className="form-control" id="InputDescription" placeholder="Бөлімнің сипаттамасы"></textarea>
           </div>

           <div className="input-group mb-3" >
               <input name="video" onChange={handleFileChange} type="file" className="form-control" id="inputGroupFile03" aria-label="Upload" ></input>
           </div>

           <div className="form-group">
             <textarea name="remarks" onChange={handleChange} className="form-control" id="InputTechnologies" placeholder="Ескертулер"></textarea>
           </div>
           
           <div className="form-group">
             <button type="button" onClick={submitForm}>Жүктеу</button>
           </div>

         </form>
        

        </>
    );
}
export default AddChapter;
