import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import UserSidebar from "./UserSidebar";

const baseUrl='http://127.0.0.1:8000/api';

function UserProfile(){
    const [studentData, setStudentData] = useState([]);

    const userId=localStorage.getItem('userId');
    
    useEffect(() => {
        document.title='Profile';
        try {
            axios.get(baseUrl + '/user/' + userId + '/').then((res) => {
                setStudentData(res.data)
            })
        } catch (error) {
            console.log(error)
        }
    }, [])

    if (!studentData) {
        return <div>Loading...</div>
    }
    return(
        
        <>
        <div id="back-img">
        <div class="container mt-4" >
          <div class="row">
            <aside class="col md-3">
              <UserSidebar/>
            </aside>
            <div class="col-9">
              <div class="card">
                 <h4 id="add-word">{studentData.full_name}</h4>
                 <div class="card-body" id="profile-body">
    
        <div id="profile-el2"><img src={studentData.profile_img} id="user-img2" className="card-img-top" alt="..."></img></div>

        <div id="profile-el2">
    
<table id="profile-table" className="table table-bordered">
                <thead>
                    <tr>
                        <th scope="col">Пошта</th>
                        <td>{studentData.email}</td>
                    </tr>
                    <tr>
                        <th scope="col">Қолданушы аты</th>
                        <td>{studentData.username}</td>
                    </tr>
                    <tr>
                        <th scope="col">Қызығушылығы</th>
                        <td>{studentData.interested_categories}</td>
                    </tr>
                </thead>
            </table>
    
        </div>
                </div>
              </div>
            </div> 
          </div>
        </div>
        </div>
</>
    );
}
export default UserProfile;
