import UserHeader from "./UserHeader";
import { useState, useEffect, useHistory  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";


const baseUrl='http://127.0.0.1:8000/api';

function TakeQuiz(){
    useEffect(()=>{
        document.title='Questions';
    });
    
    const [questionData, setQuestionData] = useState([]);
    const { quiz_id } = useParams();
    const userId = localStorage.getItem('userId');
    const history = useHistory();
  
    useEffect(() => {
      try {
        axios.get(baseUrl + '/quiz-questions/' + quiz_id + '/1').then((res) => {
          setQuestionData(res.data);
        });
      } catch (error) {
        console.log(error);
      }
    }, []);
  
    const submitAnswer = (question_id, right_ans) => {
      const _formData = new FormData();
      _formData.append('user', userId)
      _formData.append('question', question_id)
      _formData.append('right_ans', right_ans)
  
      try {
        axios.post(baseUrl + '/attempt-quiz', _formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        }).then((res) => {
          if (res.status === 200) {
            try {
              axios.get(baseUrl + '/quiz-questions/' + quiz_id + '/next-question/' + question_id).then((res) => {
                if (res.data.length === 0) {
                  // If there are no more questions, redirect to the quiz result page
                  history.push(`/quiz-result/${quiz_id}`);
                } else {
                  setQuestionData(res.data);
                }
              });
            } catch (error) {
              console.log(error);
            }
          }
        });
      } catch (error) {
        console.log(error);
      }
    };


    return(
        <>
        <UserHeader />
        <h1>Quiz title</h1>
        {questionData.map((row, index) => (
          <>
        <h4>{row.question}</h4>
        <table className="table">
         <tbody>
         
        
            <tr>
               <td><button onClick={()=>submitAnswer(row.id, row.ans1)} className="btn btn-outline-primary">{row.ans1}</button></td>
            </tr>
            <tr>
               <td><button onClick={()=>submitAnswer(row.id, row.ans2)} className="btn btn-outline-primary">{row.ans2}</button></td>
            </tr>
            <tr>
               <td><button onClick={()=>submitAnswer(row.id, row.ans3)} className="btn btn-outline-primary">{row.ans3}</button></td>
            </tr>
            <tr>
               <td><button onClick={()=>submitAnswer(row.id, row.ans4)} className="btn btn-outline-primary">{row.ans4}</button></td>
            </tr>
           
        
         </tbody>
        </table>
        </>
        ))}
        </>
    );
}
export default TakeQuiz;
