import { useState, useEffect  } from "react";
import axios from "axios";
import {useParams } from "react-router-dom";
import TeacherSidebar from "./TeacherSidebar";

const baseUrl='http://127.0.0.1:8000/api';

function QuestionDetail(){
  
  useEffect(()=>{
    document.title='Сұрақ';
  }); 
    let {question_id}=useParams();
    const [questionData, setquestionData] = useState([])

  useEffect(()=>{

      try{
        axios.get(baseUrl + '/questions/' + question_id).then((res)=>{
            setquestionData(res.data);
            
        });
      }catch(error){
        console.log(error);
      }

}, []);


    return(
        <>

<div id="back-img">
        <div className="container mt-4" >
          <div className="row">
            <aside className="col md-3">
              <TeacherSidebar/>
            </aside>
            <div className="col-9">
              <div className="card">
                 <h4 id="add-word">{questionData.question}</h4>
        <div className="card-body" id="question">
<table className="table table-bordered pb-1">
<thead>
  <tr>
    <th id="ans-table">Нұсқа 1</th>
    <td>{questionData.ans1}</td>
  </tr>
  <tr>
    <th id="ans-table">Нұсқа 2</th>
    <td>{questionData.ans2}</td>
  </tr>
  <tr>
    <th id="ans-table">Нұсқа 3</th>
    <td>{questionData.ans3}</td>
  </tr>
  <tr>
    <th id="ans-table">Нұсқа 4</th>
    <td>{questionData.ans4}</td>
  </tr>
  <tr id="a">
    <th id="ans-table">Дұрыс жауап</th>
    <td >{questionData.right_ans}</td>
  </tr>
</thead>
</table>
</div>
              </div>
            </div> 
          </div>
        </div>
        </div>
          

        </>
    );
}
export default QuestionDetail;
