import { useState, useEffect  } from "react";
import axios from "axios";
import TeacherSidebar from "./TeacherSidebar";
import Swal from "sweetalert2";

const baseUrl='http://127.0.0.1:8000/api';

function AddQuiz() {

    useEffect(()=>{
        document.title='Тест қосу';
      });

    const [quizData, setquizData] = useState({
        title: '',
        detail: ''
      });

    
    const handleChange=(event)=>{
        setquizData({
          ...quizData,
          [event.target.name]: event.target.value
        });
      }


      const submitForm=()=>{
        const teacherId=localStorage.getItem('teacherId');
        const _formData = new FormData()
        _formData.append("teacher", teacherId)
        _formData.append("title", quizData.title)
        _formData.append("detail", quizData.detail)
    
        try{
          axios.post(baseUrl+'/quiz/', _formData, {}).then((res)=>{
            if(res.status===200 || res.status===201){
              Swal.fire({
                title: 'Тест сәтті қосылды!',
                icon: 'success',
                iconColor: 'lightpink',
                toast: true,
                timer: 5000,
                position: "top-right",
                timerProgressBar: true,
                showConfirmButton: false
              });
              window.location.href='/teacher-add-quiz';
            }
          });
        }catch(error){
            console.log(error);
            
        }
      };

    return (
        <>
        <div id="back-img">
        <div class="container mt-4" >
          <div class="row">
            <aside class="col md-3">
              <TeacherSidebar/>
            </aside>
            <div class="col-9">
              <div class="card">
                 <h4 id="add-word">Тест қосу</h4>
                 <div class="card-body">
        <form>
           
           <div className="form-group">
             <input name="title" onChange={handleChange} type="text" className="form-control" id="InputTitle" placeholder="Тесттің аты"></input>
           </div>

           <div className="form-group">
             <textarea name="detail" onChange={handleChange} className="form-control" id="InputDescription" placeholder="Толық ақпарат"></textarea>
           </div>
           
           <div className="form-group">
             <button type="button" id="login-button" onClick={submitForm}>Жүктеу</button>
           </div>

         </form>
         </div>
              </div>
            </div> 
          </div>
        </div>
        </div> 
        </>
    );
}
export default AddQuiz;
