import { useState, useEffect  } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import Swal from "sweetalert2";

const baseUrl='http://127.0.0.1:8000/api';

function TeacherForgotPassword() {
    useEffect(()=>{
        document.title='Forgot Password';
      });

    const [teacherData, setteacherData] = useState({
      'email': ''
    });
    const [successMsg, setsuccessMsg] = useState([]);
    const [errorMsg, seterrorMsg] = useState([]);

          //Change Element Value
  const handleChange=(event)=>{
    setteacherData({
      ...teacherData,
      [event.target.name]: event.target.value
    });
  }

  const submitForm=()=>{
    const teacherFormData = new FormData();
    teacherFormData.append("email", teacherData.email)

    try{
      axios.post(baseUrl + '/teacher-forgot-password/', teacherFormData).then((response)=>{
        if(response.data.bool===true){
            setsuccessMsg(response.data.msg);
            seterrorMsg('');
        }else{
            seterrorMsg(response.data.msg);
            setsuccessMsg('');
        }
      });
    }catch(error){
        console.log(error);
        setteacherData({'status': 'error'});
    }  

  };

  const teacherLoginStatus=localStorage.getItem('teacherLoginStatus')
  if(teacherLoginStatus=='true'){
    window.location.href='/teacher-profile';
  }



    return (
        <>
        {/* Left Part */}
       <div className="leftSide"></div>
        
        {/* Right Part */}
       <div className="rightSide">
       <div id = "register-info">
         {/*teacherData.status ==='success' && <p className="text-success">Сәтті тіркелдіңіз!</p>*/}
         {/*teacherData.status ==='error' && <p className="text-danger">Тіркелу мүмкін емес, тағы да байқап көріңіз!</p>*/}
         <h3>Тіркелген поштаңызды жазыңыз</h3>
                 
                 {successMsg && <p className='text-success'>{successMsg}</p>}
                 {errorMsg && <p className='text-danger'>{errorMsg}</p>}
                 <div className="form-group">
                       <input name="email" type="email" value={teacherData.email} onChange={handleChange} className="form-control" id="InputPassword1" placeholder="Email"></input>
                 </div>
         
                 <div className="form-group">
                       <button id="login-button" onClick={submitForm} type="submit">Send</button>
                 </div>
         
         </div>
      </div>
 
        </>
    );
}
export default TeacherForgotPassword;
