import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";

const baseUrl='http://127.0.0.1:8000/api';

function AvailableCourses(){
  let {course_id}=useParams();
  
  console.log(course_id); 

    useEffect(()=>{
        document.title='Available Courses';
    });

    const [courseData, setCourseData] = useState([]);
    

    useEffect(()=>{
        try{
            axios.get(baseUrl + '/course/').then((res)=>{
                setCourseData(res.data);
            });
          }catch(error){
            console.log(error);
          }
    }, []);

    

    const enrollCourse=() => {
        const userId=localStorage.getItem('userId');
        const _formData = new FormData();
        _formData.append("course", course_id)
        _formData.append("user", userId)
    
        try{
          axios.post(baseUrl+'/user-enroll-course/', _formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
          }).then((res)=>{
            console.log(res.data);
            //window.location.href='/teacher-add-course';
          });
        }catch(error){
            console.log(error);
            
        }
    }

    return(
      <>
     <div id="background1">
      <div className="container mt-4" >
        <div id="my"><h4>Курстар</h4></div>    
        <div className="row mb-4">
            {courseData && courseData.map((course, index) => 
          <div className="col-md-4 mb-3">
           <div className="card" id="my-courses-cards">
            <img src={course.featured_img} className="card-img-top" alt="..."></img>
            <div className="card-body">
             <h5 className="card-title">{course.title}</h5>
             <p className="card-text">Үйренушілер саны: 123</p>
             <button onClick={enrollCourse} type='button' className="btn btn-success">Enroll</button>
            </div>
           </div>
         </div>
           )}
        </div>
     </div>  
 


     </div> 
      </>
    );
}
export default AvailableCourses
